INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54614, 0, 0, 'Бинты грязные. Давай-ка тебя перевяжем заново.', 12, 0, 100, 0, 0, 0, 67777, 0, 'Мишка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54615, 0, 0, 'Видел, как ты $gосматривал:осматривала, корабль и $gбился:билась, с монстрами. Ты просто молодец. Удалось что-нибудь найти?', 12, 0, 100, 1, 0, 0, 67491, 0, 'Узлик Шифролом to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54615, 1, 0, 'Ясно. Я догадываюсь, каким будет его следующий шаг.', 12, 0, 100, 0, 0, 0, 58269, 0, 'Узлик Шифролом to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54615, 2, 0, 'Сюда, $n! Твой гирокоптер готов к вылету!', 12, 0, 100, 3, 0, 5763, 68502, 0, 'Узлик Шифролом to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54617, 0, 0, 'Мы из Альянса и не причиним вам вреда. Скажи мне, что это была за тень, от которой ты меня избавил?', 12, 0, 100, 1, 0, 32672, 67741, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54617, 1, 0, 'Ты не понимаешь... Здесь идет война.', 12, 0, 100, 1, 0, 32673, 67743, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54855, 0, 0, 'Master Shang has trained you well. Thank you, friend!', 12, 0, 100, 0, 0, 0, 53265, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54855, 1, 0, 'I couldn\'t have fought them off alone. Now, if you\'ll excuse me, I should go find my cart.', 12, 0, 100, 0, 0, 0, 56452, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54855, 2, 0, 'Cart!', 14, 0, 100, 0, 0, 0, 56453, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54868, 0, 0, 'Посмотрим, что ты можешь без своих дружков-грязней.', 14, 0, 100, 0, 0, 0, 58598, 0, 'Вождь Гукгут');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54868, 1, 0, 'Тупые, тупые, тупые, строганые дукеры!', 14, 0, 100, 0, 0, 0, 61260, 0, 'Вождь Гукгут');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54894, 1, 0, 'Brother, I see great darkness looming before you.', 41, 0, 100, 0, 0, 0, 55032, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54894, 2, 0, 'Everywhere I look, the rivers have dried.', 41, 0, 100, 0, 0, 0, 55033, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54894, 3, 0, 'But there is yet one path, One river, that may carry you through these difficult times.', 41, 0, 100, 0, 0, 0, 55034, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54894, 4, 0, 'Powerful strangers come from across the Sea. Seek their strength. Add it to our own.', 41, 0, 100, 0, 0, 0, 55035, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54924, 0, 0, 'About time someone challenged Zhi-Zhi!', 14, 0, 100, 1, 0, 0, 53203, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54924, 1, 0, 'Alright, enough, enough! STOP HITTING ZHI-ZHI!', 12, 0, 100, 1, 0, 0, 53204, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54936, 1, 0, 'Сдавайся или умри, глупец!', 14, 0, 100, 0, 0, 0, 53380, 0, 'Гормальский налетчик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54959, 0, 0, 'Как нужно махать этой штукой? Ай!', 12, 0, 100, 6, 0, 0, 55429, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54959, 1, 0, 'Именно то, что нужно.', 12, 0, 100, 15, 0, 0, 55424, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54959, 2, 0, 'Мне нужно что-то для защиты.', 12, 0, 100, 273, 0, 0, 55427, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54959, 3, 0, 'О да! Поскорее бы в бой!', 12, 0, 100, 274, 0, 0, 55545, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54959, 4, 0, 'Мне нужно снаряжение попрочнее.', 12, 0, 100, 274, 0, 0, 55428, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54959, 5, 0, 'Мне нужно немедленно начать тренировку.', 12, 0, 100, 15, 0, 0, 55425, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54959, 6, 0, 'Это пригодится лекарю, а не мне!', 12, 0, 100, 1, 0, 0, 55426, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54959, 7, 0, 'Это... Это оружие для слабаков! Мне нужно побольше.', 12, 0, 100, 11, 0, 0, 55432, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54989, 0, 0, 'Мы научим тебя, как нужно служить!', 12, 0, 100, 0, 0, 0, 60982, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54989, 1, 0, 'Я научу тебя уважению!', 12, 0, 100, 0, 0, 0, 60985, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54989, 2, 0, 'Кланяйся!', 12, 0, 100, 0, 0, 0, 60983, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54989, 3, 0, 'Знай свое место!', 12, 0, 100, 0, 0, 0, 133177, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54989, 4, 0, 'Слабым нет места в этом мире!', 12, 0, 100, 0, 0, 0, 60984, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54989, 5, 0, '$GДерзкий:Дерзкая, $gмаленький:маленькая, |3-6($r)!', 12, 0, 100, 0, 0, 0, 60987, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54990, 0, 0, 'Надеюсь, сад не погиб...', 12, 0, 100, 0, 0, 0, 53555, 0, 'Связанный фермер Сладкого Ветерка to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54990, 1, 0, 'Большое спасибо!', 12, 0, 100, 0, 0, 0, 53554, 0, 'Связанный фермер Сладкого Ветерка to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (54990, 2, 0, 'Когда все закончится, я вернусь в столицу.', 12, 0, 100, 0, 0, 0, 53559, 0, 'Связанный фермер Сладкого Ветерка to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 0, 0, 'Вы всего лишь жалкие крестьяне с палками! Знайте свое место!', 14, 0, 100, 0, 0, 0, 58560, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 1, 0, 'Еще один доброволец хочет служить Властелину Грома.', 12, 0, 100, 0, 0, 0, 53338, 0, 'Поработитель Гормал');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 2, 0, 'Наша империя будет купаться в лучах славы! Но ты этого не увидишь!', 14, 0, 100, 0, 0, 0, 60989, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 3, 0, 'Из тебя получится отличный каменотес!', 12, 0, 100, 0, 0, 0, 58556, 0, 'Поработитель Гормал');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 4, 0, 'Я научу тебя покорности!', 14, 0, 100, 0, 0, 0, 58558, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 5, 0, 'Неповиновения мы не потерпим!', 14, 0, 100, 0, 0, 0, 60990, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 6, 0, 'Ты $gпосмел:посмела, бросить вызов могу? Тогда ты умрешь!', 14, 0, 100, 0, 0, 0, 60988, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 7, 0, 'Дерзкие щенки! Сдавайтесь или умрите!', 14, 0, 100, 0, 0, 0, 58557, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55016, 8, 0, 'Как ты смеешь дерзить мне! Знай свое место!', 14, 0, 100, 0, 0, 0, 58559, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55025, 0, 0, 'Дело сделано, но здесь нам оставаться нельзя. Встретимся на восточной границе лагеря.', 12, 0, 100, 0, 0, 0, 53333, 0, 'Дерзкий Шао');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55193, 0, 0, 'Ой-ой!.. Вождь будет недоволен...', 12, 0, 100, 397, 0, 0, 57970, 0, 'Шаман из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55193, 1, 0, 'Вам, шнуропсам, место в яме!', 12, 0, 100, 92, 0, 0, 57969, 0, 'Шаман из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55193, 2, 0, 'Дукеры!', 12, 0, 100, 25, 0, 0, 57980, 0, 'Шаман из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55234, 0, 0, 'Эй, господин Железное Брюхо! Ведьма превратила моих друзей в глыбы нефрита. Ты поможешь их выручить?', 12, 0, 100, 0, 0, 0, 55746, 0, 'Ань Воздушный Мех');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55274, 0, 0, 'Надеюсь, Нефритовая ведьма не сцапала всех моих друзей. Она такая страшная!', 12, 0, 100, 0, 0, 0, 54173, 0, 'Ань Воздушный Мех to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55274, 1, 0, 'Ты $gтакой:такая, $gсмелый:смелая,! Научишь меня, как стать смелой?', 12, 0, 100, 0, 0, 0, 54175, 0, 'Ань Воздушный Мех to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55274, 2, 0, 'Я с тобой. Можешь быть моим телохранителем!', 12, 0, 100, 0, 0, 0, 54174, 0, 'Ань Воздушный Мех to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55282, 0, 0, 'Я... <всхлипывает>... Я не могу дальше! Малыш Лу, расскажи, что произошло потом.', 12, 0, 100, 18, 0, 27885, 53997, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55283, 0, 0, 'Потеря енота – не самая страшная беда. Больше всего нас обеспокоил тот окаменевший орк... Я могу рассказать, что было дальше.', 12, 0, 100, 1, 0, 27464, 54129, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55283, 1, 0, 'Релл, пожалуйста, побереги силы. О том, что было дальше, может рассказать Салли.', 12, 0, 100, 1, 0, 27457, 53989, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 0, 0, 'Вскоре после этого со мной подружился еще один енот.', 42, 0, 100, 0, 0, 27876, 54033, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 1, 0, 'Осмотрев три святилища, я повернул обратно к лагерю.', 42, 0, 100, 0, 0, 27880, 54039, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 2, 0, 'Нет, но я могу его сделать... Командир, а топор нужен для рубки деревьев или врагов?', 12, 0, 100, 6, 0, 27858, 54001, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 3, 0, 'Я назвал его Штуковина.', 42, 0, 100, 0, 0, 27872, 54028, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 4, 0, 'В северном святилище я никого не нашел – ни Белой Пешки, ни хозенов... никого.', 42, 0, 100, 0, 0, 27879, 54038, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 5, 0, 'Стелька пошел следом.', 42, 0, 100, 0, 0, 27881, 54040, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 6, 0, 'Мы двигались на север и вышли на опушку.', 42, 0, 100, 0, 0, 27856, 54019, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 7, 0, 'Есть!', 12, 0, 100, 66, 0, 27859, 114071, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 8, 0, 'Когда я вернулся, то обнаружил, что на Релла снова напали хозены!', 42, 0, 100, 0, 0, 27726, 54067, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 9, 0, 'У юго-западного святилища я не обнаружил никаких следов Белой Пешки.', 42, 0, 100, 0, 0, 27870, 54026, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 10, 0, 'Зато я встретил дружелюбного енота, который увязался за мной.', 42, 0, 100, 0, 0, 27871, 54027, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 11, 0, 'Вперед, Стелька. Уходим отсюда, пока тебя не пристрелили.', 12, 0, 100, 1, 0, 27878, 54035, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 12, 0, 'Эмбер, зачем?! Я ведь его не подсвечивал!', 14, 0, 100, 5, 0, 27875, 54032, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 13, 0, 'Эй, малыш! Я назову тебя Штуковина.', 12, 0, 100, 3, 0, 27873, 54031, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 14, 0, 'Нет! Штуковина!', 14, 0, 100, 5, 0, 27874, 54029, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 15, 0, 'Нет... Релл...', 12, 0, 100, 0, 0, 27884, 54044, 0, 'Салли "Рассольный" Маклири to 55589');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 16, 0, 'Недолго думая я назвал его Стелька.', 42, 0, 100, 0, 0, 27877, 54034, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 17, 0, 'Да уж, Эмбер лучше не злить!', 12, 0, 100, 0, 0, 27865, 54013, 0, 'Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 18, 0, 'Хотел подкрасться к старине Салли, а?', 12, 0, 100, 0, 0, 27867, 54015, 0, 'Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55286, 19, 0, 'Отличный выстрел, Эмбер!', 12, 0, 100, 0, 0, 27862, 54010, 0, 'Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55290, 0, 0, 'Тьма...', 12, 0, 100, 0, 0, 0, 105798, 0, 'Своенравный предок');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55291, 0, 0, 'Мой сосуд ждет тебя!', 12, 0, 100, 0, 0, 0, 66899, 0, 'Кромсатель духов Шань\'цзэ to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55291, 1, 0, 'Не шевелись, осталось недолго...', 12, 0, 100, 0, 0, 0, 66903, 0, 'Кромсатель духов Шань\'цзэ to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55291, 2, 0, 'Хороший дух... думаю, мне такой пригодится.', 12, 0, 100, 0, 0, 0, 66905, 0, 'Кромсатель духов Шань\'цзэ to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55335, 0, 0, 'Я приготовился к битве.', 42, 0, 100, 0, 0, 27719, 54022, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55335, 1, 0, 'Несколько часов мы шли по лесу... на север.', 42, 0, 100, 0, 0, 27713, 53990, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55335, 2, 0, 'Увидев странное поваленное дерево, мы остановились.', 42, 0, 100, 0, 0, 27714, 53994, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55335, 3, 0, 'Малыш Лу, не дай Эмбер погибнуть! А ты, Салли, сделай так, чтобы самолет не взорвался!', 12, 0, 100, 397, 0, 27717, 53839, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55335, 4, 0, '|TInterface\\Icons\\inv_weapon_shortblade_37.blp:32|t Мощный удар$b$b|TInterface\\Icons\\inv_weapon_shortblade_92.blp:32|t Более мощный удар$b$b|TInterface\\Icons\\ability_rogue_shadowstrikes.blp:32|t Самый мощный удар', 42, 0, 100, 0, 0, 0, 68789, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55335, 5, 0, 'Внимание! Хозены устроили нам засаду!', 12, 0, 100, 22, 0, 27716, 53838, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55335, 6, 0, 'Я буду сдерживать их, сколько смогу!', 12, 0, 100, 22, 0, 27718, 53840, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55335, 7, 0, 'Салли, как думаешь, что случилось с этим деревом?', 12, 0, 100, 6, 0, 27715, 53831, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55337, 0, 0, 'Что-то в него врезалось, сэр. Его явно не рубили и оно не падало само.', 12, 0, 100, 1, 0, 27849, 53832, 0, 'Салли "Рассольный" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55337, 1, 0, 'Это самолет Эмбер и Нимм! Может, они еще живы!', 14, 0, 100, 5, 0, 27853, 53835, 0, 'Салли "Рассольный" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55337, 2, 0, 'Ты что, шутишь? Посмотри на себя – ты же едва жива!', 12, 0, 100, 5, 0, 27855, 53884, 0, 'Салли "Рассольный" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55337, 3, 0, 'Она еще дышит! Сюда! Поговорите с ней!', 14, 0, 100, 5, 0, 27854, 53837, 0, 'Салли "Рассольный" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55337, 4, 0, 'Нет, это самолет. Погоди-ка... САМОЛЕТ?!', 12, 0, 100, 5, 0, 27852, 53834, 0, 'Салли "Рассольный" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55338, 0, 0, 'Нам за ним идти? И кто такие Эмбер и Нимм?', 12, 0, 100, 6, 0, 29870, 53836, 0, 'Малыш Лу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55338, 1, 0, 'Белую Пешку? Я думал, мы охотимся на хозенов.', 12, 0, 100, 6, 0, 29871, 53886, 0, 'Малыш Лу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55338, 2, 0, 'А что это вон там? Какая-то птица?', 12, 0, 100, 6, 0, 29869, 53833, 0, 'Малыш Лу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55343, 0, 0, 'В этом нет необходимости. Я пойду с вами.', 12, 0, 100, 1, 0, 27455, 53883, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55343, 1, 0, 'Все нормально, Салли. Мои раны затянутся. Сейчас наша задача – найти Белую Пешку.', 12, 0, 100, 1, 0, 27456, 53885, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55354, 0, 0, 'М-м-м... Он назвал меня агентом.', 12, 0, 100, 1, 0, 29872, 53999, 0, 'Малыш Лу to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55355, 0, 0, 'Салли! Малыш Лу! Кто-нибудь!', 14, 0, 100, 1, 0, 27728, 58590, 0, 'Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55355, 1, 0, 'Помогите!', 14, 0, 100, 1, 0, 27726, 140174, 0, 'Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55355, 2, 0, 'Деревьев, Салли. Малыш Лу, возьмешь топор и нарубишь дров.', 12, 0, 100, 1, 0, 27723, 54002, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55355, 3, 0, 'Так, агенты, слушайте меня.', 12, 0, 100, 1, 0, 27721, 53998, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55355, 4, 0, 'Салли, осмотри святилища на западе. Постарайся найти хоть какие-нибудь следы Белой Пешки.', 12, 0, 100, 25, 0, 27724, 54004, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55355, 5, 0, 'Уже поздно, нам нужно разбить лагерь. Салли, у тебя топор есть?', 12, 0, 100, 1, 0, 27722, 54000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55355, 6, 0, 'Эмбер, а ты заберись повыше и прикрой Салли огнем. Встречаемся здесь.', 12, 0, 100, 1, 0, 27725, 54005, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55370, 0, 0, 'Портал сработал, и нас не разорвало на части. Хорошо, а то я боялся, что еще один маяк перегрузит сеть.', 12, 0, 100, 0, 0, 78232, 123883, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55371, 0, 0, 'И вдруг я понял, что происходит... но на секунду опоздал.', 42, 0, 100, 0, 0, 29880, 54094, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55371, 1, 0, 'Релл выжил, но дела его были плохи.', 42, 0, 100, 0, 0, 29874, 54071, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55371, 2, 0, 'Вся лужайка была заставлена небольшими нефритовыми статуями. Они были...почти как живые.', 42, 0, 100, 0, 0, 29877, 54084, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55371, 3, 0, 'Среди них была одна особенная. Никогда подобного не видел.', 42, 0, 100, 0, 0, 29878, 54085, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55371, 4, 0, 'Мы осмотрели статуи, и тут из дома вышла женщина-пандарен.', 42, 0, 100, 0, 0, 29879, 54115, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55371, 5, 0, 'Знак был не очень-то... располагающим ко входу.', 42, 0, 100, 0, 0, 29876, 54083, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55371, 6, 0, 'Агенты! Это дом пандаренов. А пандарены – наши союзники...', 12, 0, 100, 1, 0, 29875, 53899, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55371, 7, 0, 'Салли... это не статуи! Она хочет превратить тебя в камень!', 14, 0, 100, 1, 0, 29881, 54093, 0, 'Малыш Лу to Вдова Зеленая Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55372, 0, 0, 'Реллу становится хуже. Мы должны остановиться, чтобы он мог отдохнуть.', 12, 0, 100, 1, 0, 27461, 53897, 0, 'Эмбер Кеарнен to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55372, 1, 0, 'Хорошая мысль, Малыш Лу. Пойди и узнай, кто здесь живет. Возьми с собой Салли.', 12, 0, 100, 1, 0, 27462, 53900, 0, 'Эмбер Кеарнен to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55372, 2, 0, 'Да... и енота тоже. Я позабочусь о нашем командире.', 12, 0, 100, 1, 0, 27463, 53902, 0, 'Эмбер Кеарнен to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 0, 0, 'Стелька! НЕ-Е-ЕТ!', 14, 0, 100, 5, 0, 27893, 54096, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 1, 0, 'Ха! Какой-то знак не остановит меня и моего лучшего друга Стельку!', 12, 0, 100, 4, 0, 27888, 54086, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 2, 0, 'Если бы Мишка была здесь, она бы его мигом заштопала.', 12, 0, 100, 6, 0, 27886, 53898, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 3, 0, 'Минуточку! Это кто – орк?! Значит... и здесь Орда?!', 12, 0, 100, 6, 0, 27890, 54088, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 4, 0, 'И Стельку!', 12, 0, 100, 6, 0, 27887, 53901, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 5, 0, 'Ого! Сколько тут статуй...', 12, 0, 100, 6, 0, 27889, 54087, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 6, 0, 'Беги, Малыш Лу! Не дай ей до тебя добраться!', 14, 0, 100, 5, 0, 27894, 54127, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 7, 0, 'О чем ты говоришь?', 12, 0, 100, 274, 0, 27892, 54092, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55374, 8, 0, 'Сударыня, я дворф. Можно одолжить у вас еды?', 12, 0, 100, 24, 0, 27891, 54090, 0, 'Салли "Рассольный" Маклири to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55381, 0, 0, 'Мы охотно примем бородатого духа в семью!', 12, 0, 100, 0, 0, 31400, 54091, 0, 'Вдова Зеленая Лапа to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55381, 1, 0, 'Поглядите, кто пришел... Цзинь-юй и... Кто же это? Какой-то бородатый дух?', 12, 0, 100, 6, 0, 31399, 54089, 0, 'Вдова Зеленая Лапа to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55381, 2, 0, 'А-ХА-ХА-ХА-ХА!', 14, 0, 100, 0, 0, 31401, 89650, 0, 'Вдова Зеленая Лапа to Малыш Лу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55387, 0, 0, 'Нам предстоит пересечь поле битвы. Займись хозенами, а я побегу вперед. Если все получится, встретимся в моей деревне.', 12, 0, 100, 0, 0, 30640, 58025, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55387, 1, 0, 'Мой народ не любит чужаков, но тебе я очень обязан. Надеюсь, старейшины не накажут меня за этот поступок.', 12, 0, 100, 0, 0, 4674, 53796, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55405, 0, 0, 'Я напрягла зрение и стала читать по губам. Он сказал:', 42, 0, 100, 0, 0, 27469, 54137, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55405, 1, 0, 'Нам нужно было сообщить об этом жителям деревни Жемчужного Плавника... но сначала мы решили подстрелить парочку орков.', 42, 0, 100, 0, 0, 27472, 54145, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55405, 2, 0, 'Я поднялась на вершину горы и приготовилась стрелять из своей снайперской винтовки.', 42, 0, 100, 0, 0, 27466, 54133, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55405, 3, 0, 'Как прикажете, командир.', 12, 0, 100, 66, 0, 27467, 74770, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55405, 4, 0, 'Мы разбили лагерь в горах. Малыш Лу следил за Реллом.', 42, 0, 100, 0, 0, 27465, 54132, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55405, 5, 0, 'Салли расставил бочки со взрывчаткой – а я должна была по ним стрелять.', 42, 0, 100, 0, 0, 27470, 54140, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55405, 6, 0, 'Как я и предполагала... Орда уже здесь, в Пандарии. И они заключили союз с хозенами.', 42, 0, 100, 0, 0, 27471, 54144, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55405, 7, 0, 'Салли уже был на краю деревни.', 42, 0, 100, 0, 0, 27468, 54136, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55407, 0, 0, 'Убей побольше хозенов, Эмбер.', 12, 0, 100, 0, 0, 27730, 54134, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 0, 0, 'Это вам за Эмбер!', 12, 0, 100, 5, 0, 27898, 54148, 0, 'Салли "Рассольный" Маклири to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 1, 0, 'А это за Штуковину и за Стельку!', 12, 0, 100, 5, 0, 27900, 54150, 0, 'Салли "Рассольный" Маклири to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 2, 0, 'Что-то есть... Ящик Орды! Прикрой меня, я выйду на открытое место!', 12, 0, 100, 5, 0, 27903, 54143, 0, 'Салли "Рассольный" Маклири to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 3, 0, 'У меня подарочки для обезьян! Эмбер, постарайся меня не взорвать!', 12, 0, 100, 5, 0, 27897, 54141, 0, 'Салли "Рассольный" Маклири to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 4, 0, 'Вижу проход! Эмбер, я иду!', 12, 0, 100, 5, 0, 27904, 54146, 0, 'Салли "Рассольный" Маклири to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 5, 0, 'Застрели всех стражников, и тогда я смогу пройти дальше!', 12, 0, 100, 5, 0, 27895, 54475, 0, 'Салли "Рассольный" Маклири to Эмбер Кеарнен');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 6, 0, 'Это вам за Релла!', 12, 0, 100, 5, 0, 27899, 54149, 0, 'Салли "Рассольный" Маклири to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 7, 0, 'Отлично получилось! А теперь еще раз!', 12, 0, 100, 5, 0, 27896, 54139, 0, 'Салли "Рассольный" Маклири to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 8, 0, 'Здесь ничего нет! Идем дальше!', 12, 0, 100, 5, 0, 27902, 54142, 0, 'Салли "Рассольный" Маклири to Салли "Рассольный" Маклири');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55408, 9, 0, 'Ах! Осторожно!', 14, 0, 100, 5, 0, 27901, 54147, 0, 'Салли "Рассольный" Маклири to Взрывной бочонок Салли');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55413, 0, 0, 'Тебя ищет Тоя. Он собирает весь лишний нефрит, который у нас есть.', 12, 0, 100, 0, 0, 0, 61152, 0, 'Ань Воздушный Мех to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55461, 0, 0, 'Ты умрешь! Таков приказ Орды!', 12, 0, 100, 0, 0, 0, 61250, 0, 'Резервист Грукин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55470, 0, 0, 'Нет империалистическим шнуропсам!', 12, 0, 100, 407, 0, 0, 62313, 0, 'Хозен-задира to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55470, 1, 0, 'Вторжение шнуропсов! Небесный огонь!', 12, 0, 100, 407, 0, 0, 62314, 0, 'Хозен-задира to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55470, 2, 0, 'Вбей шнуропсов в землю по самые финтипоны!', 12, 0, 100, 407, 0, 0, 62312, 0, 'Хозен-задира to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55470, 3, 0, 'Это земля хозенов! Нет чужакам!', 12, 0, 100, 407, 0, 0, 62115, 0, 'Хозен-задира to Player');
