INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55873, 3, 0, 'Гнать тупых хозиней с нашей фермы. Вон!', 12, 0, 100, 25, 0, 0, 67315, 0, 'Косозуб-смутьян');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55873, 4, 0, 'Ешь репу, Муно!', 12, 0, 100, 25, 0, 0, 67314, 0, 'Косозуб-смутьян');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55873, 5, 0, 'У тебя тупое лицо, Муно!', 12, 0, 100, 25, 0, 0, 67313, 0, 'Косозуб-смутьян');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (55873, 6, 0, 'Вонючий хозинь!', 12, 0, 100, 25, 0, 0, 67312, 0, 'Косозуб-смутьян');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56062, 0, 0, 'Вдова Зеленая Лапа – не ведьма. Она просто делает очень правдоподобные статуи из нефрита.', 12, 0, 100, 0, 0, 0, 55754, 0, 'Тцзу Железное Брюхо to Ань Воздушный Мех');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56110, 0, 0, 'Итак, друзья... Как мы найдем тайного мастера?', 12, 0, 100, 6, 0, 31908, 60483, 0, 'Сяо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56110, 1, 0, 'Угу.', 12, 0, 100, 273, 0, 32054, 82258, 0, 'Сяо to Ли Ли');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56110, 2, 0, 'Линь, вот как ты думаешь, почему его называют "тайным мастером"?', 12, 0, 100, 1, 0, 31909, 56384, 0, 'Сяо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56110, 3, 0, 'Отлично. А я, как самый старший и самый толстый, буду ждать вас здесь.', 12, 0, 100, 273, 0, 31910, 56394, 0, 'Сяо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 0, 0, 'Какого бортеля?', 12, 0, 100, 0, 0, 0, 57169, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 1, 0, 'Это... это убирай?', 12, 0, 100, 0, 0, 0, 57171, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 2, 0, 'Да... Бесстрашный... Звучи хорошо.', 12, 0, 100, 0, 0, 0, 57172, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 3, 0, 'Так, теперь надо проверяй на кого-то...', 12, 0, 100, 0, 0, 0, 57149, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 4, 0, 'Ты, калама, подойти! У Кен-Кена тебе есть штука-дрюка!', 12, 0, 100, 0, 0, 0, 57150, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 5, 0, 'Эй, $p, ходи сюда! Кен-Кен придумать, как делай негрустный панда!', 14, 0, 100, 0, 0, 0, 57441, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 6, 0, 'Никто не грустить, если носи такая маска! На ком бы проверить?', 12, 0, 100, 0, 0, 0, 57162, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 7, 0, 'Выпей вот это.', 12, 0, 100, 0, 0, 0, 57157, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 8, 0, 'Ха-ха-ха! Получайся! Кен-Кен так и знавай, что сработать! Ты же лучше ощущай?', 14, 0, 100, 0, 0, 0, 57158, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 9, 0, 'Ой, бесхозенечно! Это отсюда убирай! Кен-Кен слишком много умный, не умирать!', 12, 0, 100, 0, 0, 0, 57170, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56115, 10, 0, 'Хорошо! Дружок, сюда ходи. За тот раз извинять! Надевай надо это вот.', 12, 0, 100, 0, 0, 0, 57163, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56138, 0, 0, 'Невероятно! Он бросил меня с этим странным грязным типом!', 12, 0, 100, 0, 0, 30420, 55485, 0, 'Ли Ли to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56138, 1, 0, 'Мы уже можем идти? Ты допил свое пиво?', 12, 0, 100, 1, 0, 30379, 55070, 0, 'Ли Ли');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56138, 2, 0, 'Неудивительно, что это заняло столько времени. Сложно что-то исследовать, сидя на одном месте.', 12, 0, 100, 0, 0, 0, 54995, 0, 'Ли Ли to Ли Ли');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56138, 3, 0, 'Подожди, ты сказал... Буйных Портеров?', 12, 0, 100, 6, 0, 30418, 55475, 0, 'Ли Ли to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56138, 4, 0, 'Потому что рядом с этими двумя ты кажешься себе худым.', 12, 0, 100, 25, 0, 30382, 54998, 0, 'Ли Ли to Ли Ли');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56184, 0, 0, 'Отдавай все овощи!', 12, 0, 100, 15, 0, 0, 54873, 0, 'Косозуб-вредитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56184, 1, 0, 'А-А-АЙ!', 12, 0, 100, 53, 0, 0, 103761, 0, 'Косозуб-вредитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56184, 2, 0, '%s в панике пытается бежать!', 16, 0, 100, 53, 0, 0, 1150, 0, 'Косозуб-вредитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56184, 3, 0, 'Теперь это земля гну-синей, уходи!', 12, 0, 100, 53, 0, 0, 60661, 0, 'Косозуб-вредитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56184, 4, 0, 'Уходи!', 12, 0, 100, 53, 0, 0, 101910, 0, 'Косозуб-вредитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56185, 0, 0, 'У нас лучше овощи! Ваши хуже!', 12, 0, 100, 0, 0, 0, 66821, 0, 'Косозуб-трудяга to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56185, 1, 0, '%s в панике пытается бежать!', 16, 0, 100, 0, 0, 0, 1150, 0, 'Косозуб-трудяга');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56194, 0, 0, '%s убегает!', 16, 0, 100, 0, 0, 0, 89989, 0, 'Паучонок');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56204, 0, 0, 'Greetings, Chen, Li Li. You may stay, but I do need any of your ale.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56206, 0, 0, 'You would champion the cause of such a lowly street rat? Very well, best me in combat and I shall consider giving you my blessing.', 12, 0, 100, 1, 0, 0, 54781, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56206, 1, 0, 'Enough! You win... champion. Tell that upstart that he has my blessing.', 12, 0, 100, 1, 0, 0, 0, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56208, 3, 0, 'Не трогай моих овец!', 14, 0, 100, 0, 0, 3580, 55079, 0, 'Пастух Франсис to Волк-налетчик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56209, 0, 0, ' At last! A challenge! Let us test your mettle, "champion".', 14, 0, 100, 1, 0, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56209, 1, 0, ' That was a fine battle! The likes of which I have not enjoyed in many decades. Go and give the boy my blessing.', 14, 0, 100, 1, 0, 0, 0, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56210, 0, 0, 'A Wanderbrew wants to marry an Goldendraft?! Preposterous.', 12, 0, 100, 1, 0, 0, 54788, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56210, 1, 0, 'I yield! The boy chose his champion well. Give him my blessing and make sure he does not squander it.', 12, 0, 100, 1, 0, 0, 0, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56236, 0, 0, 'Смерть была совсем близко! Спасибо, $gнезнакомец:незнакомка,.', 12, 0, 100, 18, 0, 0, 64344, 0, 'Раненый матрос to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56236, 1, 0, 'Мне удалось выжить только благодаря тебе... Я этого не забуду.', 12, 0, 100, 18, 0, 0, 64342, 0, 'Раненый матрос to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56236, 2, 0, 'Я... Я у тебя в долгу.', 12, 0, 100, 0, 0, 0, 55139, 0, 'Раненый матрос to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56236, 3, 0, 'Благодарю за спасение.', 12, 0, 100, 2, 0, 0, 131568, 0, 'Раненый матрос to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56267, 0, 0, 'Похоже, увиденное укрепило твою решимость. Значит, я сделал все, что было в моих силах.', 12, 0, 100, 4, 0, 30062, 64690, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56268, 0, 0, 'Искра должна находиться в центре!', 42, 0, 100, 0, 0, 30055, 64687, 0, 'Медитирующий to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56269, 0, 0, 'Ах, извини. Но ведь я тебя не напугал, да? Мы, пандарены, ценим спокойствие в любой ситуации.', 12, 0, 100, 1, 0, 30058, 64129, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56269, 1, 0, 'О, хорошо. Похоже, тебя нелегко отвлечь. Мы, пандарены, живем настоящим. Порой даже во вред себе.', 12, 0, 100, 1, 0, 30057, 64128, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56269, 2, 0, 'Ты только посмотри! Просто образец спокойствия. Я так и знал, что в тебе это есть.', 12, 0, 100, 1, 0, 30060, 64131, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56269, 3, 0, 'А теперь старайся сохранять нужный настрой. Ты можешь сосредоточиться или расслабиться, но что бы ни случилось – не теряй внутреннего равновесия.', 12, 0, 100, 0, 0, 30055, 64127, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56269, 4, 0, 'А как насчет... ЭТОГО?!', 12, 0, 100, 274, 0, 30059, 64130, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56269, 5, 0, 'Чувствуешь абсолютный покой в душе? Напиток начал действовать.', 12, 0, 100, 5, 0, 30061, 64148, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56269, 6, 0, 'Негативные эмоции: злость, сомнение – имеют странную силу здесь, в Пандарии. Скоро ты это поймешь.', 12, 0, 100, 6, 0, 0, 64146, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56310, 0, 0, 'Ты... не уйдешь!', 14, 0, 100, 0, 0, 0, 55203, 0, 'Туманный ужас to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56312, 0, 0, 'Хорошо вам провести время у Грязной Кружки! И спасибо еще раз!', 14, 0, 100, 2, 0, 31200, 55372, 0, 'Шан Громовая Пята to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56343, 0, 0, 'I am called Chen, and this is my niece, Li Li.', 12, 0, 100, 0, 0, 0, 57066, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56343, 1, 0, 'May we stay at your farmhouse for a while? We do not have much in the way of money, but I will gladly share some of my famous ale with you.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56343, 2, 0, 'Thank you! We will not trouble you for long.', 12, 0, 100, 0, 0, 0, 57069, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56344, 0, 0, 'Ну наконец-то!', 12, 0, 100, 5, 0, 30373, 57065, 0, 'Ли Ли to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56344, 1, 0, 'Еще одна остановка? Дядя Чэнь, мы же не прошли и десяти метров!', 12, 0, 100, 5, 0, 30374, 57071, 0, 'Ли Ли to Чэнь Буйный Портер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56345, 0, 0, 'Смотрите, кто здесь! Госпожа Манн, я хочу вас кое с кем познакомить.', 12, 0, 100, 0, 0, 30088, 61030, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56345, 1, 0, 'Наберитесь терпения, госпожа Манн. Эти чужаки не перестают меня удивлять.', 12, 0, 100, 1, 0, 30091, 61071, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56346, 0, 0, 'Honey-Bear! I mean... it is about time.', 12, 0, 100, 0, 0, 0, 61221, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56346, 1, 0, 'Don\'t you "snow lily" me! I was- WE- were worried sick about you.', 12, 0, 100, 0, 0, 0, 61222, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56346, 2, 0, 'And the delivery is not for ME, Hao! The honorable Lorewalker has been waitin- oh...', 12, 0, 100, 0, 0, 0, 61225, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56346, 3, 0, 'Я все понимаю. Но работа над Змеиным Сердцем идет с запаздыванием, и у нас нет лишнего нефрита.', 12, 0, 100, 1, 0, 29534, 61066, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56346, 4, 0, 'Я бы с удовольствием помогла тебе, хранитель истории, но сейчас мне трудно!', 12, 0, 100, 0, 0, 29533, 61028, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56347, 0, 0, 'Over here!', 12, 0, 100, 0, 0, 32863, 143887, 0, 'Hao Mann');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56348, 0, 0, 'Я буду и дальше искать нефрит. Если хочешь, обойди местных и предложи им свою помощь.', 12, 0, 100, 0, 0, 0, 63569, 0, 'Тоя to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56349, 0, 0, 'Хи-хи!', 12, 0, 100, 0, 0, 0, 111861, 0, 'Злобный дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56349, 1, 0, 'У-у-у!', 12, 0, 100, 0, 0, 0, 131861, 0, 'Злобный дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56349, 2, 0, 'Мое!', 12, 0, 100, 0, 0, 0, 61341, 0, 'Злобный дух to Злобный дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56398, 1, 0, 'Thank you. Let us begin.', 12, 0, 100, 0, 0, 0, 58129, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56398, 2, 0, 'Ancient spirits of water.', 12, 0, 100, 0, 0, 0, 55045, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56398, 3, 0, 'Hear my echo through the silent deeps.', 12, 0, 100, 0, 0, 0, 55046, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56398, 4, 0, 'Awaken from the great waters beyond.', 12, 0, 100, 0, 0, 0, 55047, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56398, 5, 0, 'Aid your children in this time of need!', 12, 0, 100, 0, 0, 0, 55048, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56398, 6, 0, 'A great darkness? Could he mean that we must trust these strangers... these... Alliance... after all?', 12, 0, 100, 0, 0, 0, 55049, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56418, 0, 2, 'Найди лекарей и помоги им в бою. Они должны заниматься раной.', 14, 0, 100, 0, 0, 27285, 66061, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56418, 0, 0, 'Оставайся поблизости и защищай лекарей! От этого зависят наши жизни!', 14, 0, 100, 0, 0, 27287, 66062, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56418, 0, 1, 'Если лекари погибнут, Шэнь-Цзынь Су умрет, и все мы вместе с ним. Защищай их!', 14, 0, 100, 0, 0, 27288, 66063, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56440, 0, 0, 'Полегче, Мисс Фанни. Ты бьешь слишком сильно... Арбузы взрываются!', 12, 0, 100, 0, 0, 0, 55062, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56440, 1, 0, 'Неплохо, Мисс Фанни! Отличный удар!', 12, 0, 100, 0, 0, 0, 55066, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56440, 2, 0, 'Ха-ха-ха! Даже полпути не прошел!', 12, 0, 100, 0, 0, 0, 55063, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56440, 3, 0, 'Отлично, Мисс Фанни! Прекрасно двигаешься!', 12, 0, 100, 0, 0, 0, 55065, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56440, 4, 0, 'Вы, гну-сини, воняете как тухлая репа! Хуже дукера!', 12, 0, 100, 25, 0, 0, 67317, 0, 'Батрак Муно');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56440, 5, 0, 'Ого! Неплохо стреляешь, Мисс Фанни!', 12, 0, 100, 0, 0, 0, 55064, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56440, 6, 0, 'Сейчас получишь лопатой по башке!', 12, 0, 100, 25, 0, 0, 67316, 0, 'Батрак Муно');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56440, 7, 0, 'Фанни, ты же сильная! В следующий раз постарайся как следует!', 12, 0, 100, 0, 0, 0, 55061, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56442, 0, 0, 'Даже после того как сгнила наша плоть, надежду высасывало из наших душ. Эта была бесконечная пытка!', 12, 0, 100, 0, 0, 0, 55131, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56442, 1, 0, 'Я содрогаюсь при одной мысли об этом. Древнее зло. Враг Пандарии. Я не осмелюсь произнести его имя.', 12, 0, 100, 0, 0, 0, 55132, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56442, 2, 0, 'Затем пришла тьма. Она просачивалась из земли, медленно заражала... душила нас!', 12, 0, 100, 0, 0, 0, 55130, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56442, 3, 0, 'Наконец-то мы освободились и вышли из тьмы! О, прекрасный, благословенный свет!', 12, 0, 100, 0, 0, 0, 55127, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56442, 4, 0, 'Мы охраняли воды. В них заключена слишком большая сила – они не должны попасть в чужие руки.', 12, 0, 100, 0, 0, 0, 55129, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56442, 5, 0, 'Тысячу жизней назад я принес сюда эти воды – дар Жемчужному Плавнику за подвиги, совершенные в незапамятные времена.', 12, 0, 100, 0, 0, 0, 55128, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56442, 6, 0, '"Песнь Долины" висит на стене. Возьми ее. Я слышу, меня зовут небеса. Спасибо тебе...', 12, 0, 100, 0, 0, 0, 58191, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56443, 0, 0, 'ММ-М!!!', 12, 0, 100, 0, 0, 32864, 55211, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56443, 1, 0, 'Уверен, ты прекрасно здесь справишься. Я буду снаружи.', 12, 0, 100, 1, 0, 32866, 55213, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56443, 2, 0, 'Я же говорил, что у меня все под контролем!', 12, 0, 100, 0, 0, 32865, 55212, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56444, 0, 0, 'В конце концов остается только тишина.', 12, 0, 100, 0, 0, 0, 55089, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56444, 1, 0, 'Тьма ждет...', 12, 0, 100, 0, 0, 0, 55086, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56444, 2, 0, 'Никто не смеет приближаться к этим водам!', 12, 0, 100, 0, 0, 0, 55088, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56444, 3, 0, 'Достойное жертвоприношение для нашего господина.', 12, 0, 100, 0, 0, 0, 55085, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56444, 4, 0, 'Отчаяние...', 12, 0, 100, 0, 0, 0, 55083, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56444, 5, 0, 'Порча поглотит тебя, как когда-то поглотила нас...', 12, 0, 100, 0, 0, 0, 55087, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56444, 6, 0, 'Живым здесь не место!', 12, 0, 100, 0, 0, 0, 55090, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56444, 7, 0, 'Ты не захватишь наши воды!', 12, 0, 100, 0, 0, 0, 55084, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56461, 0, 0, 'Быть может, этот дух что-то знает о стихотворении. Очистись, дух!', 12, 0, 100, 0, 0, 0, 55135, 0, 'Андуин Ринн to Player');
