INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56463, 0, 0, 'Теперь защищать воды Дола придется тебе...', 12, 0, 100, 2, 0, 0, 58186, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56463, 1, 0, 'Спасибо, $gнезнакомец:незнакомка,...', 12, 0, 100, 2, 0, 0, 55110, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56463, 2, 0, 'Тени исчезли... Порча снята.', 12, 0, 100, 2, 0, 0, 55112, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56463, 3, 0, 'Не трать эту воду зря. Она бесценна...', 12, 0, 100, 2, 0, 0, 55109, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56463, 4, 0, 'Тьма поднялась из глубин. Она голодна...', 12, 0, 100, 2, 0, 0, 58187, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56463, 5, 0, 'Наконец-то я... обрету свободу...', 12, 0, 100, 2, 0, 0, 55104, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56463, 6, 0, 'Свет... возвращается...', 12, 0, 100, 2, 0, 0, 55106, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56463, 7, 0, 'Мы защитили воды от сил тьмы.', 12, 0, 100, 2, 0, 0, 55114, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56465, 0, 0, 'Сурки придут в ярость, когда увидят, что их норы засыпаны – но ты просто пинай их ногами, и они тебя не тронут.', 12, 0, 100, 1, 0, 0, 60484, 0, 'Ана Громовая Пята to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56465, 1, 0, 'Пошли отсюда!', 12, 0, 100, 0, 0, 0, 55124, 0, 'Ана Громовая Пята');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56465, 2, 0, 'Прочь, сурки!', 12, 0, 100, 0, 0, 0, 55122, 0, 'Ана Громовая Пята');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56465, 3, 0, 'Прочь!', 12, 0, 100, 0, 0, 0, 135117, 0, 'Ана Громовая Пята');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56467, 0, 0, 'Hoh-hohhh! What a rush! I like you, $c. You keep things interesting.', 12, 0, 100, 0, 0, 0, 61219, 0, 'Hao Mann to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56467, 1, 0, 'Snow lily! We\'re back from the quarry! Got a special delivery just for you, my sweetness.', 12, 0, 100, 0, 0, 0, 61220, 0, 'Hao Mann to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56467, 2, 0, 'Sorry to keep you waiting, sugarfur. I know how you worry. Truth is, this stranger here saved my old hide. Go figure', 12, 0, 100, 0, 0, 0, 61223, 0, 'Hao Mann to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56510, 0, 0, 'Держись!', 12, 0, 100, 0, 0, 32867, 99010, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56510, 1, 0, 'Проклятые твари, никак не отстанут!', 12, 0, 100, 0, 0, 32869, 61089, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56510, 2, 0, 'Мы почти добрались до фрески! Так держать!', 12, 0, 100, 0, 0, 32870, 61090, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56510, 3, 0, 'Мерзкие кусаки! $P, задай им жару!', 12, 0, 100, 0, 0, 32868, 61088, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56537, 0, 2, 'You\'re in for it now!', 14, 0, 100, 0, 0, 0, 57123, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56537, 0, 1, 'You come to fight my brood? You come to steal my treasures?', 14, 0, 100, 0, 0, 0, 57121, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56537, 0, 0, 'Time for Chufa\'s surprise!', 14, 0, 100, 0, 0, 0, 57122, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56537, 1, 0, 'Uh oh... my battle armor... weakening...', 12, 0, 100, 0, 0, 0, 57132, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56538, 0, 0, 'NO! It\'s TURNIP!', 12, 0, 100, 1, 0, 0, 55265, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56538, 0, 1, 'NO! Not turnip!!', 12, 0, 100, 1, 0, 0, 55267, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56538, 0, 2, 'No, we been tricked!', 12, 0, 100, 1, 0, 0, 57173, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56538, 0, 3, 'Why we not eating carrot?', 12, 0, 100, 1, 0, 0, 60486, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56538, 0, 4, 'You no take carrot! You take turnip instead!', 12, 0, 100, 1, 0, 0, 54877, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56565, 0, 0, 'Проклятые кусаки крушат все подряд!', 12, 0, 100, 0, 0, 0, 58586, 0, 'Рудокоп Зеленой Скалы to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56571, 0, 0, 'Ну что ж, полагаю, мы и так уже здесь задержались.', 12, 0, 100, 0, 0, 33313, 55367, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56571, 1, 0, 'Мы с радостью поможем тебе справиться с гну-синями, Грязная Кружка!', 12, 0, 100, 66, 0, 29702, 55387, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56571, 2, 0, 'Эй, прошу прощения!', 12, 0, 100, 3, 0, 29699, 55380, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56571, 3, 0, 'До встречи, Шан.', 12, 0, 100, 3, 0, 29697, 55371, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56571, 4, 0, 'У Громовой Пяты хорошо, но вот пива у них на всех и полбочонка не наберется.', 12, 0, 100, 1, 0, 29695, 55368, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56571, 5, 0, 'Мы ищем пандарена по прозвищу Грязная Кружка. Нам туда?', 12, 0, 100, 6, 0, 29700, 55381, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56571, 6, 0, 'Я Чэнь, а это моя племянница Ли Ли.', 12, 0, 100, 2, 0, 29701, 57066, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56571, 7, 0, 'Пойдем, поищем пива, а, Ли Ли?', 12, 0, 100, 6, 0, 29696, 55369, 0, 'Чэнь Буйный Портер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56572, 0, 0, 'Я же говорила, что он странный.', 12, 0, 100, 0, 0, 30411, 55392, 0, 'Ли Ли to Чэнь Буйный Портер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56572, 1, 0, 'Ну, если пиво заставит тебя встать, тогда ладно. Идем искать пиво.', 12, 0, 100, 1, 0, 30402, 55370, 0, 'Ли Ли to Чэнь Буйный Портер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56572, 2, 0, 'Ха, разве только из-за нашей компании!', 12, 0, 100, 15, 0, 30405, 55375, 0, 'Ли Ли to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56572, 3, 0, 'Ну конечно! На Скитающемся острове я перебила миллион гну-синей!', 12, 0, 100, 1, 0, 30409, 55386, 0, 'Ли Ли to Чэнь Буйный Портер');
