INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56611, 0, 0, 'Я немедленно туда пойду. Ли Ли... оставайся здесь. Я за тобой вернусь.', 12, 0, 100, 25, 0, 29722, 55482, 0, 'Чэнь Буйный Портер to Legacy Controller');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56611, 1, 0, 'В Пандарии есть Буйные Портеры?', 12, 0, 100, 6, 0, 29719, 55476, 0, 'Чэнь Буйный Портер to Legacy Controller');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56611, 2, 0, 'Грязная Кружка! Отличное пиво! И какое крепкое!', 12, 0, 100, 11, 0, 29718, 55473, 0, 'Чэнь Буйный Портер to Legacy Controller');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56611, 3, 0, 'Обещай мне, что не будешь ввязываться в неприятности, пока меня нет.', 12, 0, 100, 404, 0, 29723, 55483, 0, 'Чэнь Буйный Портер to Legacy Controller');
