INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56611, 5, 0, 'Хмелевары... моя семья... родной дом...', 12, 0, 100, 273, 0, 29720, 55478, 0, 'Чэнь Буйный Портер to Legacy Controller');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56614, 0, 0, 'Ну конечно! Это же самая знаменитая семья хмелеваров в долине!', 12, 0, 100, 404, 0, 30983, 55477, 0, 'Грязная Кружка to Legacy Controller');
