INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56614, 2, 0, 'Просто иди дальше по дороге, но...', 12, 0, 100, 274, 0, 30985, 55481, 0, 'Грязная Кружка to Legacy Controller');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56614, 3, 0, 'Спасибо! Местным оно нравится больше всего того, что делают на Хмелеварне Буйных Портеров.', 12, 0, 100, 24, 0, 30982, 55474, 0, 'Грязная Кружка to Legacy Controller');
