INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56785, 4, 0, 'Как следует обучайте своих змеев. Их доверие – ваше главное оружие.', 14, 0, 100, 0, 0, 0, 56186, 0, 'Лю Пламенное Сердце');
