INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56866, 0, 0, 'When everything is ready, I Seek out the pond.', 12, 0, 100, 0, 0, 0, 0, 0, 'Мудрый Асе to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56870, 0, 0, 'Это ваша вина! Вы позволили ша пробудиться после стольких лет!', 14, 0, 100, 0, 0, 27626, 55307, 0, 'Мастер Снежный Вихрь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56870, 1, 0, 'Подойдите, и мы сможем познакомиться.', 14, 0, 100, 0, 0, 27635, 59404, 0, 'Мастер Снежный Вихрь to Благоухающий Лотос');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (56872, 0, 0, 'Во имя предков!', 14, 0, 100, 0, 0, 33369, 57987, 0, 'Осон to 57080');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57119, 0, 0, 'Таких дукеров, как ты, я еще не видел!', 12, 0, 100, 407, 0, 0, 57940, 0, 'Хозен-опустошитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57119, 1, 0, 'Дукеры быстро дохнуть!', 12, 0, 100, 407, 0, 0, 57939, 0, 'Хозен-опустошитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57119, 2, 0, 'Бортельный дукер!', 12, 0, 100, 25, 0, 0, 58275, 0, 'Хозен-опустошитель');
