INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57123, 1, 0, 'Ying, Huizhong... gather your things. We\'re leaving the farm until it\'s safe.', 12, 0, 100, 0, 0, 0, 55935, 0, '');
