INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57648, 1, 0, 'Thank you! I will try to break some of that silk shield off.', 12, 0, 100, 0, 0, 0, 56358, 0, 'Mothfighter to Mothallus');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57648, 2, 0, 'Mothallus is defeated! Hurray for $p!', 12, 0, 100, 0, 0, 0, 56345, 0, 'Mothfighter to Mothallus');
