INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57713, 0, 0, 'Я попробую спросить у потоков...', 12, 0, 100, 6, 0, 30691, 56387, 0, 'Мудрый Асе to Линь Мягкая Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57714, 0, 0, 'Спросить у потоков? Асё, ты же не старейшина. Ты еще даже не взрослый! Только зря время потратишь.', 12, 0, 100, 274, 0, 31938, 56388, 0, 'Кан Ежевичный Посох to Линь Мягкая Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57714, 1, 0, 'Отличная мысль, Кен-Кен. Я пойду на юг, в джунгли.', 12, 0, 100, 1, 0, 31939, 56390, 0, 'Кан Ежевичный Посох to Линь Мягкая Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57715, 0, 0, 'Кен-Кен идти за восточным ветром. Ближе всего.', 12, 0, 100, 1, 0, 30867, 56392, 0, 'Кен-Кен to Линь Мягкая Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57715, 1, 0, 'Кен-Кен думать, надо разделиться. Идти за четырьмя ветрами.', 12, 0, 100, 1, 0, 30866, 56389, 0, 'Кен-Кен to Линь Мягкая Лапа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57716, 0, 0, 'Кто-нибудь из вас знает, где он живет?', 12, 0, 100, 6, 0, 30881, 56383, 0, 'Линь Мягкая Лапа to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57716, 1, 0, 'Ну да. Прости.', 12, 0, 100, 24, 0, 30882, 56385, 0, 'Линь Мягкая Лапа to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57716, 2, 0, 'Так откуда начнем поиски?', 12, 0, 100, 6, 0, 30883, 56386, 0, 'Линь Мягкая Лапа to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57825, 0, 0, 'Oh great! Someone else has come to bother me.', 12, 0, 100, 1, 0, 0, 57431, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57830, 0, 0, 'А я вот еще денек посплю...', 12, 0, 100, 0, 0, 0, 57432, 0, 'Сунни to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57830, 1, 0, '%s мирно храпит.', 16, 0, 100, 0, 0, 0, 71841, 0, 'Сунни to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57898, 0, 0, 'Я иду к себе. Можешь рассказывать тетушке, как "героически" $gспас:спасла, меня от самого себя... да о чем угодно.', 12, 0, 100, 0, 0, 0, 57080, 0, 'И-Мо Длинная Бровь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (57898, 1, 0, 'Ну все, все! Хватит уже толкаться!', 12, 0, 100, 0, 0, 0, 57072, 0, 'И-Мо Длинная Бровь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58068, 0, 0, 'Преклони колена, проситель!', 12, 0, 100, 0, 0, 0, 57189, 0, 'Разведчик из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58068, 1, 0, 'Отведи взгляд, крестьянин!', 12, 0, 100, 0, 0, 0, 57193, 0, 'Разведчик из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58068, 2, 0, 'Властелин Грома вернется, и тогда вам не устоять!', 12, 0, 100, 0, 0, 0, 57191, 0, 'Разведчик из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58071, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 1150, 0, 'Trained Compy');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58071, 1, 0, '%s в панике пытается бежать!', 16, 0, 100, 0, 0, 0, 1150, 0, 'Дрессированный компсогнат');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58113, 0, 0, 'Пока нельзя, Леза рожает. Надо подождать.', 12, 0, 100, 0, 0, 0, 62751, 0, 'Служитель солнца Дезко to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58113, 1, 0, 'Это не должно было быть боевым заданием.', 12, 0, 100, 0, 0, 0, 62753, 0, 'Служитель солнца Дезко to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58113, 2, 0, 'Какая часть нашего путешествия создала у тебя впечатление, что у нас есть выбор?', 12, 0, 100, 0, 0, 0, 62752, 0, 'Служитель солнца Дезко to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58114, 0, 0, 'Дезко, мы должны нанести удар сейчас, когда могу считают нас слабыми. Давай спустим на них |3-3($c).', 12, 0, 100, 0, 0, 0, 57949, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58114, 1, 0, 'Дезко, надо продолжать идти на запад. Здесь мы уязвимы.', 12, 0, 100, 0, 0, 0, 62756, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58114, 2, 0, 'Я благодарен твоей жене, старина, за то, что она привела нас сюда. Но она выбрала неудачное время для родов.', 12, 0, 100, 0, 0, 0, 62757, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58114, 3, 0, 'Никто не знал, что Альянс станет руководствоваться точно такими же видениями, как те, что были у твоей жены.', 12, 0, 100, 0, 0, 0, 62755, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58114, 4, 0, 'Могу – прирожденные лидеры. Соблюдай осторожность.', 12, 0, 100, 0, 0, 0, 56978, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58114, 5, 0, '$p, сразись с богомолами, а мы продолжим борьбу с могу. Если повезет, мы скоро к тебе присоединимся.', 12, 0, 100, 0, 0, 0, 57494, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58114, 6, 0, 'Дезко, это оружие Орды. Тебе надо использовать его против Альянса.', 12, 0, 100, 0, 0, 0, 62754, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58117, 0, 0, 'Склонись пред нами!', 12, 0, 100, 0, 0, 0, 57195, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58117, 1, 0, 'Что такое? Я занят.', 12, 0, 100, 0, 0, 0, 62769, 0, 'Искатель из Доцзяна to Искатель из Доцзяна');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58117, 2, 0, 'Этого достаточно?', 12, 0, 100, 0, 0, 0, 62770, 0, 'Искатель из Доцзяна to Искатель из Доцзяна');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58117, 3, 0, 'Мы возродимся, и тогда вы будете служить нам!', 12, 0, 100, 0, 0, 0, 57190, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58117, 4, 0, 'Преклони колена, проситель!', 12, 0, 100, 0, 0, 0, 57189, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58117, 5, 0, 'Твоему народу суждено подчиниться.', 12, 0, 100, 0, 0, 0, 57194, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58117, 6, 0, 'Властелин Грома вернется, и тогда вам не устоять!', 12, 0, 100, 0, 0, 0, 57191, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58117, 7, 0, 'Положи к остальным, Лорд Искатель осмотрит их.', 12, 0, 100, 0, 0, 0, 62771, 0, 'Искатель из Доцзяна to Искатель из Доцзяна');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58146, 0, 0, 'Удерживайте их! Защищайте лучников!', 14, 0, 100, 0, 0, 0, 56892, 0, 'Защитник Змеиного Хребта to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58158, 0, 0, 'Охотничий лагерь Эрнестуэя находится в западной части Долины Четырех Ветров. Если когда-нибудь решишь отправиться на запад, заходи к нам. Удачной охоты!', 12, 0, 100, 0, 0, 0, 57200, 0, 'Даггл Бомбстер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58164, 0, 0, 'А-А-А! ММ-М!', 14, 0, 100, 0, 0, 0, 57330, 0, 'Леза Искатель Зари');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58165, 0, 0, 'Быстрее, глупцы. Эти артефакты нужны нам как воздух! Шевелитесь, а не то отведаете палки!', 14, 0, 100, 0, 0, 0, 57161, 0, 'Доминатор из Доцзяна');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58198, 0, 0, 'Ты сильнее меня. Если хочешь продолжить, найди мастера Снежного Вихря в додзё на вершине горы.', 12, 0, 100, 16, 0, 0, 62666, 0, 'Послушник Шадо-Пан');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58225, 0, 0, 'А-а, вот хорошее яичко. Да, оно подойдет. Нет, не это – оно слишком маленькое. Вот это тоже ничего...', 12, 0, 100, 0, 0, 0, 56993, 0, 'Инструктор Тун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58242, 0, 0, 'Ну ладно, хуже мне все равно не будет.', 12, 0, 100, 0, 0, 0, 57152, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58242, 1, 0, '%s держится за громко бурлящий живот.', 16, 0, 100, 0, 0, 0, 57153, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58242, 2, 0, 'Нет!', 14, 0, 100, 0, 0, 0, 135514, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58242, 3, 0, 'Ох-х... р-р-р...', 14, 0, 100, 0, 0, 0, 57159, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58242, 4, 0, 'Ой... ох... что ты со мной сделал?!', 12, 0, 100, 0, 0, 0, 57156, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58242, 5, 0, 'Кто, я?', 12, 0, 100, 0, 0, 0, 57151, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58257, 0, 0, 'Прекрас-сное оружие. Оно должно быть моим!', 12, 0, 100, 0, 0, 0, 63418, 0, 'Шкуродер Речного Клинка to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58273, 0, 0, 'Любишь цзинь-юй?!', 12, 0, 100, 0, 0, 0, 63417, 0, 'Бандит Речного Клинка to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58273, 1, 0, 'Какая блес-стящ-щая броня. Отдай ее мне!', 12, 0, 100, 0, 0, 0, 63420, 0, 'Бандит Речного Клинка to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58273, 2, 0, 'С-сдавайс-ся!', 12, 0, 100, 0, 0, 0, 63419, 0, 'Бандит Речного Клинка to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58274, 0, 0, '%s в панике пытается бежать!', 16, 0, 100, 0, 0, 0, 1150, 0, 'Налетчик Речного Клинка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58315, 0, 0, 'Начинается!', 12, 0, 100, 25, 0, 0, 57079, 0, 'Инструктор Небесный Шип to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58315, 1, 0, 'Обычно у новых учеников бывает больше времени на подготовку.', 12, 0, 100, 1, 0, 0, 57076, 0, 'Инструктор Небесный Шип to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58315, 2, 0, 'К сожалению, детеныш не хочет ждать!', 12, 0, 100, 11, 0, 0, 57077, 0, 'Инструктор Небесный Шип to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58315, 3, 0, '$n, надеюсь, ты будешь усердно трудиться. Выращивать облачного змея – великая честь.', 12, 0, 100, 2, 0, 0, 57078, 0, 'Инструктор Небесный Шип to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58341, 0, 0, 'По-моему, мы почти пришли.', 12, 0, 100, 0, 0, 0, 57088, 0, 'Грязная Кружка to Грязная Кружка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58353, 0, 0, 'Кажется, я как раз вовремя!', 12, 0, 100, 0, 0, 29758, 57126, 0, 'Чэнь Буйный Портер to Чуфа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58353, 1, 0, 'Что?!', 12, 0, 100, 53, 0, 29757, 125725, 0, 'Чэнь Буйный Портер to Чуфа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58353, 2, 0, 'Дальше ты без меня справишься. Встретимся у Грязной Кружки.', 12, 0, 100, 4, 0, 29759, 57133, 0, 'Чэнь Буйный Портер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58353, 3, 0, 'Я что-нибудь пропустил?', 12, 0, 100, 6, 0, 29756, 57124, 0, 'Чэнь Буйный Портер to Чуфа');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58377, 0, 0, 'Мы возродимся, и тогда вы будете служить нам!', 12, 0, 100, 0, 0, 0, 57190, 0, 'Красарангский мучитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58377, 1, 0, 'Властелин Грома вернется, и тогда вам не устоять!', 12, 0, 100, 0, 0, 0, 57191, 0, 'Красарангский мучитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58377, 2, 0, 'Склонись пред нами!', 12, 0, 100, 0, 0, 0, 57195, 0, 'Красарангский мучитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58377, 3, 0, 'Отведи взгляд, крестьянин!', 12, 0, 100, 0, 0, 0, 57193, 0, 'Красарангский мучитель to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58379, 0, 0, 'Ладно, нет времени. И-Мо срочно нужна помогай! Надевать ему маску!', 12, 0, 100, 0, 0, 0, 57207, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58379, 1, 0, '$p! Хорошо чувствуй? Надо вставай!', 12, 0, 100, 0, 0, 0, 57232, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58379, 2, 0, '$p, у нас получайся! Мы там все распинай-вытолкай!', 12, 0, 100, 0, 0, 0, 57265, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58379, 3, 0, 'Кен-Кен думай, еще оставайся грустняков, да...', 12, 0, 100, 0, 0, 0, 57270, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58379, 4, 0, 'Что за... Эти ша лазить отовсюду! Надо разбирайся с ними сначала!', 12, 0, 100, 0, 0, 0, 57206, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58380, 0, 0, 'Попрошайки! Захватчики! Мы обратим в прах и вас, и этих ваших пандаренов!', 14, 0, 100, 0, 0, 0, 57145, 0, 'Первопроходец Брожай to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58397, 0, 0, 'Я... просто... счастлив!', 12, 0, 100, 0, 0, 0, 57167, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58397, 1, 0, 'Фу... и что это было? Похоже на прокисший чай.', 12, 0, 100, 0, 0, 0, 57164, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58397, 2, 0, '%s тяжело вздыхает.', 16, 0, 100, 0, 0, 0, 78012, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58397, 3, 0, 'Благодарю вас, герои! Я буду рассказывать истории о вас, $p $gМогучий:Могучая, и Кен-Кен Бесстрашный!', 12, 0, 100, 0, 0, 0, 57168, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58397, 4, 0, 'Так... ну ладно. Давай сюда.', 12, 0, 100, 0, 0, 0, 57166, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58410, 0, 0, '$n... To think you\'d risked your life to save me - twice now - and I\'d only treated you with disdain.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58410, 1, 0, 'I am shamed beyond comprehension. Please forgive me - my mind was not my own.', 12, 0, 100, 0, 0, 0, 57267, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58410, 2, 0, 'Should you ever need aid of any kind, from a handful of turnip seeds to a volley of arrows, know that Zhu\'s Watch will provide it.', 12, 0, 100, 0, 0, 0, 57268, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58410, 3, 0, 'For now, I must direct my attention to rebuilding the village. There is much to be done. You may rest here if you like, but we\'ll not accept any more help.', 12, 0, 100, 0, 0, 0, 57269, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58427, 3, 0, 'Юный серобокий мушан просит о помощи!', 16, 0, 100, 0, 0, 0, 66795, 0, 'Юный серобокий мушан');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 0, 0, 'The pennants mark the race course, but you will not be penalized for finding other routes.', 12, 0, 100, 0, 0, 0, 67071, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 1, 0, 'Do you see the hanging pennants above our heads? These flags mark the race course. If you ever get lost, you can use these flags as a guide.', 12, 0, 100, 0, 0, 0, 57236, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 2, 0, 'I will guide you with this rope. If you get too far away from me, it will break and I will be forced to restart the exercise.', 12, 0, 100, 0, 0, 0, 57235, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 3, 0, 'This is the finish line. After you have flown through all 10 checkpoints, fly under the finish line to lock in your place.', 12, 0, 100, 0, 0, 0, 67075, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 4, 0, 'If you start to get too far, I will warn you. If you continue to stray, I will not hesitate to fail you.', 12, 0, 100, 0, 0, 0, 57234, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 5, 0, 'I have shown you part of the course. Now, let us see the finish line. Follow me!', 12, 0, 100, 0, 0, 0, 67073, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 6, 0, 'The faster you race, the better your prize.', 12, 0, 100, 0, 0, 0, 67081, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 7, 0, 'Remember, in a real race, you will compete against the other trainees in the Order.', 12, 0, 100, 0, 0, 0, 67074, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 8, 0, 'In a real race, you will need to fly through each checkered flag to get credit for passing the checkpoint.', 12, 0, 100, 0, 0, 0, 57251, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 9, 0, 'Students of our Order test their speed in an aerial competition known as the Sky Race.', 12, 0, 100, 0, 0, 0, 57222, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 10, 0, 'Your cloud serpent approaches, $n. When you are ready, hop on and we will begin.', 12, 0, 100, 0, 0, 0, 67030, 0, 'Instructor Windblade to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 11, 0, 'The stakes will be high.', 12, 0, 100, 0, 0, 0, 67080, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 12, 0, 'Spread among the spires are 10 hot air balloons, or checkpoints. We are passing one just now.', 12, 0, 100, 0, 0, 0, 57281, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 13, 0, 'You must fly through all 10 checkpoints before you can finish the race.', 12, 0, 100, 0, 0, 0, 57250, 0, 'Instructor Windblade to Jade Cloud Serpent');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58444, 14, 0, 'You may find that other paths are faster.', 12, 0, 100, 0, 0, 0, 67072, 0, 'Instructor Windblade to Jade Cloud Serpent');
