INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58723, 0, 0, 'You can find me in my house if you need me.', 12, 0, 100, 0, 0, 0, 57503, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58723, 1, 0, 'Take over for me... uh... what\'s your name? $n? Thank you, $n. I need a break.', 12, 0, 100, 0, 0, 0, 57502, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58729, 0, 0, 'So, you\'ve taken over the ranch for good, eh?', 12, 0, 100, 1, 0, 0, 57512, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58729, 1, 0, 'Hey, Yoon! Come out here!', 12, 0, 100, 5, 0, 0, 57511, 0, 'Haohan Mudclaw to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58729, 2, 0, 'You? Join the Tillers? Ha! You\'d have to prove your farming skills to all five members of our Council. They would never vote for you!', 12, 0, 100, 0, 0, 0, 57520, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58729, 3, 0, 'Come on boys, let\'s leave him to his "farming."  Ho ho ho!', 12, 0, 100, 11, 0, 0, 57518, 0, 'Haohan Mudclaw to 58730');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58729, 4, 0, 'Do as you please, city boy, but I wager you won\'t last a week. I mean, just look at this dump!', 12, 0, 100, 0, 0, 0, 57515, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58729, 5, 0, 'You even know how to run a farm? Know what, I\'ll save you the trouble. Sell me this here piece of land and go back where you came from.', 12, 0, 100, 25, 0, 0, 57514, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58729, 6, 0, 'Let\'s get one thing straight. You may be Old Yoon\'s cub, but you\'re still a citypaw.', 12, 0, 100, 274, 0, 0, 57513, 0, 'Haohan Mudclaw to Farmer Yoon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58730, 0, 0, 'B-but it\'s my family farm. I can\'t just sell it.', 12, 0, 100, 1, 0, 0, 57524, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58730, 1, 0, 'Oh, oh my! Mr. Mudclaw! I didn\'t expect such an important visitor.', 12, 0, 100, 21, 0, 0, 57519, 0, 'Farmer Yoon to Haohan Mudclaw');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58730, 2, 0, 'Yessir! My grandfather left me this farm. Just moved in the other day. Nice to meet you, neighbor!', 12, 0, 100, 3, 0, 0, 57522, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58730, 3, 0, 'Besides, once I\'ve gotten the hang of this, I want to be a Tiller like the rest of you!', 12, 0, 100, 0, 0, 0, 57523, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58730, 4, 0, 'You are THE Haohan Mudclaw, right? Head of the Tiller\'s Union? They are the master farmers in all of Pandaria!', 12, 0, 100, 1, 0, 0, 57521, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58730, 5, 0, 'Never say never, my grandpa always said. I\'m going to give it my best shot.', 12, 0, 100, 0, 0, 0, 57525, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58730, 6, 0, 'Hey, do I hear someone outside?', 12, 0, 100, 0, 0, 0, 57526, 0, 'Farmer Yoon to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (58733, 0, 0, 'Two days, tops!', 12, 0, 100, 0, 0, 0, 57528, 0, 'Mung-Mung to Haohan Mudclaw');
