INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60038, 15, 0, 'Please, $c, drive them out of Kun-Lai for good.', 12, 0, 100, 94, 0, 0, 59592, 0, 'Pandaren Prisoner to Player');
