INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60127, 5, 0, 'What?! I will finish you myself!', 14, 0, 100, 15, 0, 0, 59638, 0, 'Ordo Warbringer to ELM General Purpose Bunny Hide Body');
