INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60289, 0, 0, 'Да пусть кроколиски приходят. Когда-нибудь их нападения прекратятся. Зачем вмешиваться?', 12, 0, 100, 0, 0, 0, 59807, 0, 'Лени Поедатель Водорослей to Player');
