INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60503, 0, 0, 'Кафа – хорошо для груммелей и пандаренов, плохо для козлов, йети и хозенов. Пора уже запомнить!', 12, 0, 100, 0, 0, 0, 60044, 0, 'Дядя Остробоб');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60508, 0, 0, 'Know what a yak is?', 12, 0, 100, 0, 0, 0, 62498, 0, 'Zengi to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60508, 1, 0, 'They got recalled to Serpent\'s Spine.', 12, 0, 100, 0, 0, 0, 62501, 0, 'Zengi to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60508, 2, 0, 'Imagine one of those walking around on two feet, only angrier.', 12, 0, 100, 0, 0, 0, 62499, 0, 'Zengi to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60508, 3, 0, 'No Kota Kafa? How am I going to wake up in the morning?', 12, 0, 100, 0, 0, 0, 62496, 0, 'Zengi to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60508, 4, 0, 'I thought you only felt weather in your knees.', 12, 0, 100, 0, 0, 0, 62495, 0, 'Zengi to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60508, 5, 0, 'Это было перед тем, как ты собственными лапами построил эту стену, или после?', 12, 0, 100, 0, 0, 0, 62504, 0, 'Цзэньги');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60524, 6, 0, 'Эта земля суждена зандаларам.', 12, 0, 100, 0, 0, 0, 64104, 0, 'Зандаларский охотник теней to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60524, 7, 0, 'Мы возьмем то, что для нас!', 12, 0, 100, 0, 0, 0, 64105, 0, 'Зандаларский охотник теней to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60524, 8, 0, 'Эта земля останется нашей!', 12, 0, 100, 0, 0, 0, 64102, 0, 'Зандаларский охотник теней to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60524, 9, 0, 'Ты не ту сторону выбрать!', 12, 0, 100, 0, 0, 0, 64103, 0, 'Зандаларский охотник теней to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60530, 0, 0, 'Alright, my first fight!', 12, 0, 100, 1, 0, 0, 59931, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60532, 0, 0, 'Prepare for the beating of your life, weakling!', 12, 0, 100, 0, 0, 0, 59932, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60532, 1, 0, 'I am... humbled.', 12, 0, 100, 0, 0, 0, 59948, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60533, 0, 0, 'Today is the day I defeat my rival!', 12, 0, 100, 0, 0, 0, 59933, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60533, 1, 0, 'I lost to $n, twice in a row? I can\'t believe it.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60534, 0, 0, 'I apologize in advance for the beating you are about to suffer.', 12, 0, 100, 0, 0, 0, 59934, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60534, 1, 0, 'Very interesting...', 12, 0, 100, 0, 0, 0, 107292, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60535, 0, 0, 'YAAAAAAAAHHHH!!!', 12, 0, 100, 0, 0, 0, 59935, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60535, 1, 0, 'Nice moves, sweetie. Maybe I\'ll see you again...', 12, 0, 100, 0, 0, 0, 59951, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60536, 0, 0, 'Your winning streak ends here, $n. Today, you fall by my spear.', 12, 0, 100, 0, 0, 0, 59936, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60536, 1, 0, 'My father forsaw this day in the waters. He told me that I would be defeated, but I refused to believe it.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60537, 0, 0, 'I will not hold back on you, $n. Please, do me the same favor.', 12, 0, 100, 0, 0, 0, 59937, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60537, 1, 0, 'It\'s been a while since I lost to a trainee. Thank you for the match.', 12, 0, 100, 0, 0, 0, 59953, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60538, 0, 0, 'Just give up! It\'s hopeless! You\'ll never beat me!', 12, 0, 100, 0, 0, 0, 59938, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60538, 1, 0, 'How did... I don\'t... no, no NO! NOT AGAIN!', 12, 0, 100, 0, 0, 0, 59954, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60539, 0, 0, 'Good idea! Let\'s ookin\' get it on!', 12, 0, 100, 0, 0, 0, 59939, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60539, 1, 0, 'Tukka-Tuk pretty sad about losing to wimpy $n. Tukka-Tuk gonna go look at horizon and think about life.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60540, 0, 0, 'Don\'t rule me out be just cause I\'m a country boy! I got freakish strong arms, you know.', 12, 0, 100, 1, 0, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60540, 1, 0, 'Nice moves. You keep reapin\' in those wins like grain, neighbor. I\'ll be rootin\' for you.', 12, 0, 100, 1, 0, 0, 59956, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60541, 0, 0, '<sigh>...Let\'s get this over with.', 12, 0, 100, 1, 0, 0, 59941, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60541, 1, 0, 'Now THAT\'S what I was looking for! You\'ve given me a reason to train again.', 12, 0, 100, 1, 0, 0, 59957, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60542, 0, 0, 'By the name of Chi-Ji, I smite you down!', 12, 0, 100, 0, 0, 0, 59942, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60542, 1, 0, 'Impossible...', 12, 0, 100, 0, 0, 0, 51720, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60543, 0, 0, 'Give me everything you have, $n.', 12, 0, 100, 0, 0, 0, 59943, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60543, 1, 0, 'Yes... yes, I believe you will do. I will see you again shortly, $n.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60544, 0, 0, 'Ha ha! Don\'t pull any punches!', 12, 0, 100, 0, 0, 0, 59944, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60544, 1, 0, 'The taste of defeat... so strange and unfamiliar. Thank you for keeping me humble.', 12, 0, 100, 0, 0, 0, 59960, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60545, 0, 0, 'My hated rival... so glad you came.', 12, 0, 100, 0, 0, 0, 59945, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60545, 1, 0, 'Still clinging to the tailfeathers of Chi-Ji, and his idiotic notions of "hope"?', 12, 0, 100, 0, 0, 0, 60039, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60545, 2, 0, 'I will show you the meaning of despair... and I will drag this whole island down with me!', 14, 0, 100, 0, 0, 0, 60040, 0, '');
