INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60572, 1, 0, 'Хватит! Я сам с тобой справлюсь!', 14, 0, 100, 16, 0, 0, 61269, 0, 'Накк\'ракас to Накк\'ракас');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60572, 2, 0, 'Эй, моя смерть не остановит грозу, которая ждет вас! Восстанет Властелин Грома!', 14, 0, 100, 0, 0, 0, 60775, 0, 'Накк\'ракас to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60572, 3, 0, 'Армия Властелина Грома захватит эти земли, и она снова будут нашими!', 14, 0, 100, 16, 0, 0, 61231, 0, 'Накк\'ракас to Императорский гвардеец');
