INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60680, 1, 0, 'Мы... мы живы!', 12, 0, 100, 0, 0, 0, 60196, 0, 'Племянник Берберри to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60680, 2, 0, 'Ар-р!', 12, 0, 100, 0, 0, 0, 60179, 0, 'Племянник Берберри to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60680, 3, 0, 'Смотри, смотри, красотка! $gДобрый:Добрая:r, |3-6($r) $gпринес:принесла:r, тебе кафу!', 12, 0, 100, 0, 0, 0, 60177, 0, 'Племянник Берберри to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60683, 0, 0, 'Ты $gвыбрал:выбрала, не ту сторону в битве, |3-6($r). Сторону свирепых тварей!', 14, 0, 100, 0, 0, 0, 60288, 0, 'Дмонг Наруук to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60683, 1, 0, 'Нужно... вернуть... контроль...', 14, 0, 100, 0, 0, 0, 60181, 0, 'Дмонг Наруук');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60683, 2, 0, 'Нет! Я не могу сосредоточиться на факеле!', 14, 0, 100, 0, 0, 0, 60180, 0, 'Дмонг Наруук');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60684, 0, 0, 'Я не забыла! Это ты забыл, что среди этих пленных – мой муж.', 12, 0, 100, 1, 0, 0, 60310, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60684, 1, 0, 'Нужно атаковать сейчас, Бань, пока их внимание сосредоточено на подступах к Кунь-Лай.', 12, 0, 100, 0, 0, 0, 60327, 0, 'Суна Беззвучный Удар');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60684, 2, 0, 'И зачем только он оставил тебя командующим? Ты хочешь, чтобы мы спрятались под одеяло и ждали, когда яунголы постучатся в дверь?', 12, 0, 100, 0, 0, 0, 60349, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60684, 3, 0, 'Мы уже вынудили их перейти к обороне. Их становится все меньше и меньше.', 12, 0, 100, 1, 0, 0, 60309, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60684, 4, 0, 'Но там же Линь!', 12, 0, 100, 0, 0, 0, 60329, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60684, 5, 0, 'Ты забыл, что у них в плену мой муж? У нас нет времени на раздумья!', 12, 0, 100, 0, 0, 0, 60328, 0, 'Суна Беззвучный Удар to Бань Медвежье Сердце');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60687, 0, 0, 'И нас тоже. Разве ты не видела, сколько у нас раненых? Разве ты забыла, скольких забрали в плен?', 12, 0, 100, 6, 0, 0, 60313, 0, 'Бань Медвежье Сердце to Суна Беззвучный Удар');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60688, 0, 0, 'Успокойтесь оба. У нас много дел, и спасение Линя – одна из самых главных задач.', 12, 0, 100, 1, 0, 0, 60315, 0, 'Тажань Чжу to Суна Беззвучный Удар');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60689, 0, 0, 'Не может быть: |3-6($c), $gкоторый:которая,, похоже, знает, что делать на поле битвы.', 12, 0, 100, 0, 0, 0, 60355, 0, 'Защитник Лун-Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60689, 1, 0, 'Не стесняйся, |3-6($c). Яунголов хватит на всех!', 12, 0, 100, 0, 0, 0, 60357, 0, 'Защитник Лун-Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60689, 2, 0, 'Прибыло подкрепление! Ну же, |3-6($r), приди нам на помощь!', 12, 0, 100, 0, 0, 0, 60354, 0, 'Защитник Лун-Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60689, 3, 0, 'Пора тебе показаться, |3-6($r)!', 12, 0, 100, 0, 0, 0, 60353, 0, 'Защитник Лун-Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60693, 0, 0, 'Они всю наживку откусывают, а крючок не берут.', 12, 0, 100, 0, 0, 0, 60202, 0, 'Рыболов с верфи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60694, 0, 0, 'Куда катится мир! Уже даже умереть нельзя, чтобы к тебе не цеплялись крабы.', 12, 0, 100, 0, 0, 0, 63524, 0, 'Мудрец Ляо');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60697, 0, 0, 'Вырвите факел туманного шамана из земли!', 42, 0, 100, 0, 0, 0, 68351, 0, 'Осул - туманный шаман to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60730, 0, 0, 'Thank you for freeing me, brother. I cannot let you fight alone.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60734, 0, 0, 'Гол... госс...', 12, 0, 100, 0, 0, 0, 60289, 0, 'Голгосс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60735, 0, 0, 'Настоящую силу не победить обманом! Этот герой сильнее тебя!', 12, 0, 100, 0, 0, 0, 60261, 0, 'Катак Побежденный to Цзюн Дук');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60735, 1, 0, 'Уже не пытаешься сбежать, Цзюн Дук?', 12, 0, 100, 0, 0, 0, 60259, 0, 'Катак Побежденный to Цзюн Дук');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60735, 2, 0, 'Где твои подхалимы, великий вождь? На этот раз они тебе не помогут.', 12, 0, 100, 0, 0, 0, 60260, 0, 'Катак Побежденный to Цзюн Дук');
