INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60763, 1, 0, 'Я только этого и ждал! Пора познакомить яунголов с моими кулаками!', 12, 0, 100, 2, 0, 0, 60218, 0, 'Стрелок Лун-Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60763, 2, 0, 'Я знал, что кто-нибудь придет за нами! Разреши мне помочь тебе сражаться с яунголами.', 12, 0, 100, 2, 0, 0, 60225, 0, 'Стрелок Лун-Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60763, 3, 0, 'Я у тебя в долгу, $gнезнакомец:незнакомка,. Позволь помочь тебе.', 12, 0, 100, 2, 0, 0, 60219, 0, 'Стрелок Лун-Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60763, 4, 0, 'Спасибо, что $gосвободил:освободила, меня. Я не могу позволить тебе сражаться в одиночку.', 12, 0, 100, 2, 0, 0, 60220, 0, 'Стрелок Лун-Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60764, 0, 0, 'Надо сразиться с осулами. Остановить эту ненависть.', 12, 0, 100, 0, 0, 0, 60290, 0, 'Арконисс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60767, 0, 0, 'Зеркальное... отражение!', 14, 0, 100, 0, 0, 0, 60228, 0, 'Капитан Живорез');
