INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (60769, 2, 0, 'Я сам позабочусь, чтобы вы отправились на тот свет!', 14, 0, 100, 5, 0, 0, 62651, 0, 'Генерал Шо Лень to Генерал Шо Лень');
