INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61007, 0, 0, 'But... but... I needed that gear...', 12, 0, 100, 1, 0, 0, 60667, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61012, 0, 0, 'Fight well, and with honor.', 12, 0, 100, 1, 0, 0, 60560, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61012, 1, 0, 'Very well. Today... is your day...', 12, 0, 100, 1, 0, 0, 60719, 0, 'EvEndText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61013, 0, 0, 'Wikkets gonna fail. I got a box full of boom booms!', 12, 0, 100, 1, 0, 0, 60589, 0, 'EvStartText text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61015, 0, 0, 'Что... Что происходит?', 12, 0, 100, 0, 0, 0, 145187, 0, 'Ялия Мудрый Шепот to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61015, 1, 0, 'Посмотрим, какая сила оскверняет это место.', 12, 0, 100, 0, 0, 0, 60526, 0, 'Ялия Мудрый Шепот to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61015, 2, 0, 'Иди за мной! Мы очистим землю при помощи тотемов.', 12, 0, 100, 0, 0, 0, 60529, 0, 'Ялия Мудрый Шепот to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61015, 3, 0, 'Помоги мне. Активируй три больших тотема.', 12, 0, 100, 0, 0, 0, 60527, 0, 'Ялия Мудрый Шепот to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61020, 0, 0, 'Сюда летит целая стая! Скорее – бери мое оружие!', 12, 0, 100, 5, 0, 0, 61954, 0, 'Повелитель ястребов Нужун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61020, 1, 0, 'Молодец! Бежим вниз и добьем его.', 12, 0, 100, 22, 0, 0, 61957, 0, 'Повелитель ястребов Нужун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61020, 2, 0, 'Сюда! Мне пригодится помощь!', 12, 0, 100, 22, 0, 0, 61952, 0, 'Повелитель ястребов Нужун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61020, 3, 0, 'Что это... Что это такое? Он что, правда пытается перелететь?', 12, 0, 100, 22, 0, 0, 61955, 0, 'Повелитель ястребов Нужун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61020, 4, 0, 'Приближается! Cбей его!', 12, 0, 100, 22, 0, 0, 61956, 0, 'Повелитель ястребов Нужун to Player');
