INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61038, 1, 0, 'Он сильнее меня. Он велит мне убить вас.', 14, 0, 100, 0, 0, 0, 63326, 0, 'Ян Гоши');
