INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61042, 0, 0, 'Страх застилает мой разум. Я... я больше не могу сопротивляться.', 14, 0, 100, 0, 0, 0, 63321, 0, 'Чэн Кан');
