INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61050, 1, 0, 'Во мне было... столько ненависти...', 12, 0, 100, 2, 0, 0, 60570, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61050, 2, 0, 'Что? Как странно, я чувствую покой...', 12, 0, 100, 2, 0, 0, 60572, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61050, 3, 0, 'Я... Что случилось? Мне теперь намного спокойнее.', 12, 0, 100, 2, 0, 0, 60575, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61050, 4, 0, 'Спасибо, $gмой избавитель:моя избавительница,. Без тебя мне не удалось бы избежать цепких лап ша.', 12, 0, 100, 2, 0, 0, 60573, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61050, 5, 0, 'Неужели мной завладели ша? До этой минуты мне это и в голову не приходило...', 12, 0, 100, 2, 0, 0, 60576, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61050, 6, 0, 'Так это были... Ша? Мне так хотелось разорвать всех на куски!', 12, 0, 100, 2, 0, 0, 60574, 0, 'Обезумевший следопыт Шадо-Пан to Испепеляющая ненависть');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61065, 0, 0, 'Пора, друг мой. Приготовься к бою!', 12, 0, 100, 0, 0, 0, 61018, 0, 'Говорящий с водой Гожай to Источник порчи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61065, 1, 0, 'Он мертв! Получилось!', 14, 0, 100, 0, 0, 0, 61103, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61065, 2, 0, 'Друг, я возвращаюсь к беженцам. Найди нас там, чтобы мы смогли как следует тебя отблагодарить.', 12, 0, 100, 0, 0, 0, 61104, 0, 'Говорящий с водой Гожай to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61065, 3, 0, 'Если уничтожить взрывную ненависть, то его щит исчезнет! Быстрее!', 12, 0, 100, 5, 0, 0, 68558, 0, 'Говорящий с водой Гожай to Шай Ху');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61065, 4, 0, 'Я начинаю ритуал. Приготовься!', 12, 0, 100, 0, 0, 0, 61017, 0, 'Говорящий с водой Гожай to Источник порчи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61066, 0, 0, 'Где часовые на северной стороне? Я не увидел ни одного, когда подъезжал к лагерю. Ты что-то расслабился, Гао-Жань.', 12, 0, 100, 1, 0, 0, 61754, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61067, 0, 0, 'Благодарю вас, господин Чжу. Удачной охоты!', 12, 0, 100, 1, 0, 0, 61771, 0, 'Гао-Жань Закаленный to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61067, 1, 0, 'Простите, господин Чжу, но это не так. На наших плечах лежит слишком много. Нас атакуют на всех фронтах, мои бойцы истощены, и у меня нет ни одного свободного воина.', 12, 0, 100, 1, 0, 0, 61755, 0, 'Гао-Жань Закаленный to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61069, 0, 0, 'Твоя ярость! Мне придает сил твоя ярость!', 14, 0, 100, 5, 0, 0, 60581, 0, 'Шай Ху');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61069, 1, 0, 'Нет! Не может быть! Ты меня не остановишь!', 14, 0, 100, 0, 0, 0, 60583, 0, 'Шай Ху');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61069, 2, 0, 'Я... что-то чувствую. Это... спокойствие?', 12, 0, 100, 0, 0, 0, 60584, 0, 'Шай Ху to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61069, 3, 0, 'Ваши ничтожные атаки тщетны!', 14, 0, 100, 0, 0, 0, 68520, 0, 'Шай Ху');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61069, 4, 0, 'Г-р-р! Ярость заполняет меня!', 14, 0, 100, 0, 0, 0, 68521, 0, 'Шай Ху to Взрывная ненависть');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61092, 0, 0, 'Ненависть переполняет вас!', 42, 0, 100, 0, 0, 0, 60579, 0, 'Испепеляющая ненависть to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61093, 1, 0, 'I will NEVER yield!', 14, 0, 100, 0, 0, 0, 63768, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61093, 2, 0, 'Thank you, $n. You have saved us all.', 14, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61093, 3, 0, 'Remember, it is at the darkest moment that light shines the brightest.', 14, 0, 100, 0, 0, 0, 63898, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61093, 4, 0, 'Well done, heroes.', 14, 0, 100, 0, 0, 0, 63899, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61118, 0, 0, 'Ветер сегодня хорош.', 12, 0, 100, 1, 0, 0, 59837, 0, 'Здоровяк Зеленое Перо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61130, 0, 0, 'Ар-р-р!', 12, 0, 100, 0, 0, 0, 82862, 0, 'Разъяренный кузнец');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61130, 1, 0, 'Не могу... обуздать... ярость!', 12, 0, 100, 0, 0, 0, 61177, 0, 'Разъяренный кузнец');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61131, 0, 0, 'Гокку-лай!', 12, 0, 100, 0, 0, 0, 60637, 0, 'Хранитель тайн Могу\'шан to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61131, 1, 0, 'Туро фон Курос!', 12, 0, 100, 0, 0, 0, 60635, 0, 'Хранитель тайн Могу\'шан to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61131, 2, 0, 'Ло-кал...', 12, 0, 100, 0, 0, 0, 60638, 0, 'Хранитель тайн Могу\'шан to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61161, 0, 0, 'Я сказал. Этот як ужасен. Мы не уйдем из этого храма яков, пока он не исправится или пока нам его не заменят.', 12, 0, 100, 0, 0, 0, 61116, 0, 'Синескок to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61161, 1, 0, 'Видишь? Плохой як! Ну-ка еще, |3-6($r). Попробуй покормить его.', 12, 0, 100, 0, 0, 0, 61110, 0, 'Синескок to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61161, 2, 0, 'Понятно. А можно хотя бы позаботиться о моем? Он даже седока не хочет нести.', 12, 0, 100, 0, 0, 0, 61114, 0, 'Синескок to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61161, 3, 0, 'Как видишь, это дефективный як. Подойди, |3-6($r). Попробуй его погладить.', 12, 0, 100, 0, 0, 0, 61108, 0, 'Синескок to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61162, 0, 0, 'Брат Синескок... Ты чрезмерно настойчив. И ты плохо ведешь себя по отношению к |3-2($r).', 12, 0, 100, 0, 0, 0, 61115, 0, 'Брат Каменная Ноша to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 0, 0, 'Помни, что не всех в Пандарии можно завоевать острием меча.', 12, 0, 100, 1, 0, 0, 63375, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 1, 0, 'Я один из хранителей истории моего народа. Мой инструмент – это слово.', 12, 0, 100, 0, 0, 31788, 62633, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 2, 0, 'Похоже... мои птицы полюбили тебя. Я последую их примеру.', 12, 0, 100, 4, 0, 31796, 63376, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 3, 0, 'Добро пожаловать! Чем я могу тебе помочь?', 12, 0, 100, 404, 0, 0, 63377, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 4, 0, 'Пойдем со мной, и мы будем учиться друг у друга. Твой народ любит сады?', 12, 0, 100, 0, 0, 31785, 62625, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 5, 0, 'Увы и ах, мне не хватает художественного дара. Подскажи мне, как украсить эту стену?', 12, 0, 100, 274, 0, 0, 62634, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 6, 0, 'Мои поручения почти закончились. Иди, я покажу тебе свою коллекцию экзотических птиц. Я даже разрешу тебе покормить их.', 12, 0, 100, 0, 0, 31794, 62654, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 7, 0, 'Значит, вы стойкие и свирепые люди. Но помни: лев убивает только, когда голоден.', 12, 0, 100, 1, 0, 0, 63373, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 8, 0, 'Нет, нет, молчи. Пусть за тебя говорит твой рисунок. Мы, пандарены, ценим красоту во всем, что нас окружает:', 12, 0, 100, 404, 0, 31789, 62646, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 9, 0, '%s протягивает вам пару садовых ножниц и указывает на большое лохматое дерево.', 16, 0, 100, 25, 0, 0, 62628, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 10, 0, 'А, вот и они, мои малышки. Видишь, как просто завести здесь новых друзей?', 12, 0, 100, 404, 0, 31795, 63374, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 11, 0, 'Ах, ясно – это один из великих воинов Альянса? На вашем гербе красуется лев.', 12, 0, 100, 1, 0, 31793, 63372, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 12, 0, 'Так-так. Кто бы мог подумать, что здесь скрывается такая красота! Теперь я вижу, что этот твой "Альянс", меняя мир, действует аккуратно. Любопытно...', 12, 0, 100, 4, 0, 31787, 62632, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 13, 0, 'Делай осторожные надрезы... Пусть дерево само решит, как ему расти.', 12, 0, 100, 1, 0, 31786, 62630, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 14, 0, 'в изгибах ствола дерева, в ярких красках восхода, даже в безупречных движениях воина с мечом.', 12, 0, 100, 273, 0, 0, 62647, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61218, 15, 0, 'Многие отрезают все, что им не нравится, но это убивает растение. Пандарены так не поступают.', 12, 0, 100, 274, 0, 0, 62631, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61252, 0, 0, 'Спасибо, $p. Ты неплохо сражаешься для $gтого:той,, кто не состоит в нашем ордене. С остальным я справлюсь сам.', 12, 0, 100, 0, 0, 0, 61270, 0, 'Страж Шадо-Пан to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61260, 0, 0, 'Давай, чемпик! Порычай Рок Рок!', 12, 0, 100, 1, 0, 0, 60743, 0, 'Тренер Рок Рок');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61260, 1, 0, 'Нет! Нет-нет-нет-нет-нет! Борец надо вставай! Вставай, шнуропс!', 12, 0, 100, 0, 0, 29027, 60906, 0, 'Тренер Рок Рок to Борец');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61289, 0, 0, 'Старая Шляпа – самый лучший тренер. Если $p послушает меня, то будет удачкой!', 12, 0, 100, 0, 0, 0, 60777, 0, 'Старая Шляпа to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61297, 0, 0, 'Гробница Завоевателей находится прямо под нами. Иди по пыльной тропинке и выйдешь ко входу. Поторопись!', 12, 0, 100, 0, 0, 0, 66969, 0, 'Образ хранителя истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61303, 0, 0, 'Принесите мне их головы!', 14, 0, 100, 0, 0, 0, 84388, 0, 'Кобай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61303, 1, 0, 'Вперед, яунголы, шагом марш! Всем трусам – смерть!', 14, 0, 100, 0, 0, 0, 60731, 0, 'Кобай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61303, 2, 0, 'Death to the Shado-Pan! Death to all Pandaren!', 14, 0, 100, 0, 0, 0, 60730, 0, 'Kobai');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61303, 3, 0, 'Смерть шадо-панам! Смерть всем пандаренам!', 14, 0, 100, 0, 0, 0, 60730, 0, 'Кобай');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61303, 4, 0, 'Нет! Что... происходит...', 14, 0, 100, 0, 0, 0, 62831, 0, 'Кобай to Player');
