INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61417, 0, 2, 'You do us a great service friend.', 12, 0, 100, 0, 0, 0, 61326, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61417, 0, 3, 'Thanks to you I\'ll be rested when those barbarians return.', 12, 0, 100, 0, 0, 0, 61327, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61434, 0, 0, 'За императрицу!', 12, 0, 100, 0, 0, 0, 63398, 0, 'Сик\'тик - боец авангарда to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61434, 1, 0, 'Пандария склонится перед императрицей!', 12, 0, 100, 0, 0, 0, 63392, 0, 'Сик\'тик - боец авангарда to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61434, 2, 0, 'Нюцзао падет!', 12, 0, 100, 0, 0, 0, 63389, 0, 'Сик\'тик - боец авангарда to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61434, 3, 0, 'Храм будет нашим!', 12, 0, 100, 0, 0, 0, 63390, 0, 'Сик\'тик - боец авангарда to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61454, 0, 0, 'Сокруши захватчиков и сбрось их со стены!', 12, 0, 100, 0, 0, 0, 61500, 0, 'Суна Беззвучный Удар to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61472, 0, 0, 'У меня все хорошо! Помоги остальным!', 12, 0, 100, 0, 0, 0, 53560, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61472, 1, 0, 'Кто бы мог подумать, что с нашим садом может случиться такое.', 12, 0, 100, 0, 0, 0, 53557, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61472, 2, 0, 'Спасибо! Мне страшно даже подумать о том, что они собирались с нами сделать.', 12, 0, 100, 0, 0, 0, 53561, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61472, 3, 0, 'Наконец-то я свободна! За это нужно выпить...', 12, 0, 100, 0, 0, 0, 53556, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61472, 4, 0, 'Надеюсь, сад не погиб...', 12, 0, 100, 0, 0, 0, 53555, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61472, 5, 0, 'А, так гораздо лучше. Повязки были таким тугими!', 12, 0, 100, 0, 0, 0, 53558, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61472, 6, 0, 'Большое спасибо!', 12, 0, 100, 0, 0, 0, 53554, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61472, 7, 0, 'Когда все закончится, я вернусь в столицу.', 12, 0, 100, 0, 0, 0, 53559, 0, 'Связанный фермер Сладкого Ветерка to Связанный фермер Сладкого Ветерка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61490, 0, 0, 'Нас трое, но оторвать тебе голову я могу и один.', 12, 0, 100, 0, 0, 0, 61371, 0, 'Танкисс to Пехотинец Жу-Цзинь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61490, 1, 0, 'We were on our way to the Tiger Temple when you caught us. Lucky for you our healer was napping... apparently.', 12, 0, 100, 0, 0, 0, 61372, 0, 'Tankiss to Ruqin Infantry');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61490, 2, 0, 'Не хочешь ли кое-что "попробовать"? Подойди-ка поближе...', 12, 0, 100, 0, 0, 0, 61373, 0, 'Танкисс to Пехотинец Жу-Цзинь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61496, 0, 0, 'Я почти одурачил сам себя, поверив, что Цзоучин не грозят опасности извне.', 12, 0, 100, 1, 0, 0, 63650, 0, 'Дошу Сгибающий Сталь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61496, 1, 0, 'Когда-то я был великим бойцом. Будем надеяться, что с возрастом я не стал слабаком.', 12, 0, 100, 1, 0, 0, 63652, 0, 'Дошу Сгибающий Сталь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61504, 0, 0, 'Ветер сегодня хорош.', 12, 0, 100, 1, 0, 0, 59837, 0, 'Коротышка Чистый Воздушный Змей to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61524, 0, 0, 'Новости даже лучше, чем я надеялся услышать. Скорей бы уснуть в теплой кровати.', 12, 0, 100, 396, 0, 0, 60997, 0, 'Разведчик Вэй-чин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61525, 0, 0, 'В первую очередь нужно думать о рое. Да помогут нам предки, если богомолы решат прорваться через заставу.', 12, 0, 100, 66, 0, 0, 60995, 0, 'Разведчик Цзай-гань to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61526, 0, 0, 'Лучше, чем еда, может быть только отдых. Я думала, придется здесь еще поторчать.', 12, 0, 100, 396, 0, 0, 60998, 0, 'Разведчица Ин to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61527, 0, 0, 'Пусть тот, кто сменит меня, знает, что полчища богомолов пытались проскользнуть по главному каналу. Это непростой пост.', 12, 0, 100, 66, 0, 0, 60996, 0, 'Разведчица Лун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 0, 0, 'У меня все получится. Я знаю, что получится...', 14, 0, 100, 0, 0, 0, 62176, 0, 'Шоми');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 1, 0, '...а теперь В АТАКУ!', 14, 0, 100, 0, 0, 0, 62192, 0, 'Шоми');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 2, 0, 'Бойцовский дух сейчас как надает тебе по морде!', 14, 0, 100, 0, 0, 0, 62179, 0, 'Шоми');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 3, 0, 'Будете знать, как лезть в мою деревню!', 14, 0, 100, 0, 0, 0, 62177, 0, 'Шоми');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 4, 0, 'Отец уж слишком меня оберегает. Никогда ничего не разрешает!', 12, 0, 100, 0, 0, 0, 62181, 0, 'Шоми to Шоми');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 5, 0, 'Отлично! Сразу к делу!', 12, 0, 100, 1, 0, 0, 61836, 0, 'Шоми to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 6, 0, 'В руинах наверняка живут призраки. Хотя я в них не верю. Но мне ведь и в зандаларов не верилось.', 12, 0, 100, 1, 0, 0, 62189, 0, 'Шоми to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 7, 0, 'Спасибо тебе за тренировку, $n. Надеюсь, когда-нибудь я стану бойцом не хуже тебя!', 12, 0, 100, 2, 0, 0, 62204, 0, 'Шоми to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 8, 0, 'Надо двигаться быстро, как... Горный поток!', 14, 0, 100, 0, 0, 0, 62178, 0, 'Шоми');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61534, 9, 0, 'Я вижу, ты никогда никогда не выпускаешь цели из виду. Вот что значит концентрация!', 14, 0, 100, 0, 0, 0, 62174, 0, 'Шоми');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61541, 0, 0, 'Встаньте повыше, чтобы избежать Темного тумана.', 41, 0, 100, 4, 0, 0, 65275, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61541, 1, 0, 'Властелин Грома жив! Наши империи снова едины...', 12, 0, 100, 4, 0, 0, 68516, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61541, 2, 0, 'Все только начинается... Мы не собираемся ложиться и умирать...', 12, 0, 100, 0, 0, 0, 68784, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61541, 3, 0, 'Скормлю тебя воронам!', 12, 0, 100, 0, 0, 0, 64715, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61541, 4, 0, 'Нам нужны эти земли! Катаклизм уничтожил наш дом.', 12, 0, 100, 0, 0, 0, 68517, 0, 'Пророк Хар\'зул');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61545, 0, 0, 'Друзья говорят, что я тормоз, но я бегаю быстрее их всех!', 12, 0, 100, 0, 0, 0, 63929, 0, 'Коа to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61549, 0, 0, 'Когда в одном месте столько кланов, драки не избежать.', 12, 0, 100, 274, 0, 0, 61319, 0, 'Гуртанский задира');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61551, 0, 0, 'Я слышал, что король заставит нас сражаться друг с другом. Можно подумать, это трудная задача.', 12, 0, 100, 11, 0, 0, 61318, 0, 'Рубака клана Каргеш');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61557, 0, 0, 'Дукеры вытворять омерзюшные штуки!', 12, 0, 100, 0, 0, 0, 57974, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61557, 1, 0, 'Гукгут говорит – твоя умирай-давай!', 12, 0, 100, 0, 0, 0, 57972, 0, 'Древолаз из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61557, 2, 0, 'Брюкни их в джункс!', 12, 0, 100, 0, 0, 0, 57975, 0, 'Древолаз из племени Ловкого Хвоста to Player');
