INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61695, 0, 0, 'Спасибо за помощь! Я пойду искать своего отца.', 12, 0, 100, 6, 0, 0, 66374, 0, 'Ку-Мо to Player');
