INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61699, 1, 0, '%s остается без боеприпасов и улетает!', 41, 0, 100, 0, 0, 0, 61838, 0, 'Сик\'тик Желтокрыл to Сик\'тик Желтокрыл');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61706, 0, 0, 'Как я и думал. Спасибо вам обоим. $p, я буду ждать тебя в катакомбах.', 12, 0, 100, 0, 0, 0, 61988, 0, 'Тай Хо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61706, 1, 0, 'Как хочешь. Мы найдем твой артефакт. $p, встретимся в месте вторжения шра\'тиков.', 12, 0, 100, 0, 0, 0, 61978, 0, 'Тай Хо to Player');
