INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61793, 0, 0, 'Что такое Свет?', 12, 0, 100, 6, 0, 0, 61416, 0, 'Йо-Йо Железная Бровь to Аиса Воспевающая Облака');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61793, 1, 0, 'Давай просто... не останавливаться.', 12, 0, 100, 1, 0, 0, 61409, 0, 'Йо-Йо Железная Бровь to Аиса Воспевающая Облака');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61818, 0, 0, 'Мое!', 12, 0, 100, 381, 0, 0, 61341, 0, 'Игловертыш');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61818, 1, 0, 'Если дурак потеряет меч, то это будет уже не меч дурака, нет!', 12, 0, 100, 381, 0, 0, 61337, 0, 'Игловертыш');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61818, 2, 0, 'Кто успел, тот и съел! Хватит глупостей.', 12, 0, 100, 381, 0, 0, 61340, 0, 'Игловертыш');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61819, 0, 0, 'Лао-Чинь! Мы же победили! Почему ты плачешь?', 12, 0, 100, 6, 0, 0, 61713, 0, 'Бань Медвежье Сердце to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61820, 0, 0, 'Все эти бочки... мое лучшее пиво! Ну, по крайней мере под конец оно пользовалось взрывным успехом!', 12, 0, 100, 1, 0, 0, 61568, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61837, 0, 0, 'Добро пожаловать в Штормград!', 12, 0, 100, 3, 0, 0, 61410, 0, 'Лерия Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61838, 0, 0, 'Хе... пандарен. Наверное, хочешь присоединиться к Альянсу.', 12, 0, 100, 0, 0, 0, 61407, 0, 'Гавин Малсбери to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61838, 1, 0, 'Правильное решение. По-моему, вышло удачно.', 12, 0, 100, 0, 0, 6594, 61408, 0, 'Гавин Малсбери to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61839, 0, 0, 'Тише, Нааней. Чужаки подходят.', 12, 0, 100, 1, 0, 0, 61412, 0, 'Лукас Северинг to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61840, 0, 0, 'Смотри внимательно, Лукас. Свет в них силен. Особенно в $gнем:ней,.', 12, 0, 100, 1, 0, 0, 61414, 0, 'Нааней to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61840, 1, 0, 'Чужаки – да, но с добрыми намерениями.', 12, 0, 100, 1, 0, 0, 61413, 0, 'Нааней to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61841, 0, 0, 'Пандарен? Кажется, я брежу...', 12, 0, 100, 0, 0, 0, 61411, 0, 'Брунн Златопой to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61850, 0, 0, 'Надеюсь, $n, наши пути снова пересекутся! А пока пусть твое путешествие будет спокойным и не пройдет зря.', 12, 0, 100, 1, 0, 0, 61333, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61850, 1, 0, 'I hope our paths cross again, $n! Until then, may your journey be safe and meaningful.', 12, 0, 100, 1, 0, 0, 61333, 0, 'Lorewalker Cho to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61881, 0, 0, 'Я выдавлю из вас жизнь, мелкое отродье!', 14, 0, 100, 0, 0, 0, 66092, 0, 'Новобранец Фэн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61881, 1, 0, 'Да, лучше тебе убежать!', 14, 0, 100, 0, 0, 0, 66089, 0, 'Новобранец Фэн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61886, 0, 0, 'Прикончи его! Скинь его со стены!', 41, 0, 100, 0, 0, 0, 61723, 0, 'Опытный осул-лучник');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61895, 0, 0, 'Эй, смотри! Гноллы!', 12, 0, 100, 1, 0, 6594, 61401, 0, 'Марти to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61896, 0, 0, 'Это не гноллы, глупыш. Протри глаза.', 12, 0, 100, 1, 0, 6594, 61402, 0, 'Джози to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61896, 1, 0, 'Это фурболги.', 12, 0, 100, 1, 0, 6594, 61403, 0, 'Джози to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61962, 0, 0, 'A pandaren legend is born! Sing praises to our new hero, $n!', 14, 0, 100, 0, 0, 38119, 75115, 0, 'Lorewalker Cho to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61962, 1, 0, 'Встречайте легенду Пандарии! Воспевайте нашего нового героя!', 14, 0, 100, 22, 0, 38119, 75115, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (61962, 2, 0, 'А, мой старый друг из Нефритового леса.', 12, 0, 100, 273, 0, 0, 75046, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62004, 0, 0, 'Ваше присутствие пробудило древнего духа!', 41, 0, 100, 0, 0, 0, 61566, 0, 'Ghost Essence to Проклятая скульптура могу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62093, 0, 0, 'Две части нефти, одна часть пива... нет, нефти нужно больше...', 12, 0, 100, 0, 0, 0, 61503, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62107, 0, 0, 'Проверь, как идут дела у рекрутов. Им не помешает тренировка под руководством опытного воина.', 12, 0, 100, 0, 0, 0, 59069, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 0, 0, 'I\'m sure they are just being overly superstitious.', 12, 0, 100, 0, 0, 0, 62514, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 1, 0, 'Heartless? Is it too much to ask for a clean outhouse?', 12, 0, 100, 0, 0, 0, 62508, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 2, 0, 'Who says that?', 12, 0, 100, 0, 0, 0, 62505, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 3, 0, 'What?! Don\'t give me that look!', 12, 0, 100, 0, 0, 0, 62509, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 4, 0, 'Так кто там защищает Джутовый путь?', 12, 0, 100, 0, 0, 0, 62513, 0, 'Юна');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 5, 0, 'Hmm. I never really liked that place. The outhouses were always dirty.', 12, 0, 100, 0, 0, 0, 62507, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 6, 0, 'What\'s a yaungol?', 12, 0, 100, 0, 0, 0, 62510, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 7, 0, 'So who is protecting the Burlap Trail?', 12, 0, 100, 0, 0, 0, 62513, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 8, 0, 'Of course.', 12, 0, 100, 0, 0, 0, 138318, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 9, 0, 'No. And no kafa either. Dark days are coming.', 12, 0, 100, 0, 0, 0, 62506, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62149, 10, 0, 'Drinking is not guarding.', 12, 0, 100, 0, 0, 0, 62515, 0, 'Yoona to Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62158, 0, 0, 'Uhh...', 12, 0, 100, 0, 0, 0, 62488, 0, 'Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62158, 1, 0, 'I haven\'t seen any Shado-Pan lately.', 12, 0, 100, 0, 0, 0, 62487, 0, 'Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62158, 2, 0, 'Has there been any word from Kota Basecamp?', 12, 0, 100, 0, 0, 0, 62482, 0, 'Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62158, 3, 0, 'I heard the yaungol have taken over most of the farmlands in the south.', 12, 0, 100, 0, 0, 0, 62484, 0, 'Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62158, 4, 0, 'Hozen are on the ravage again. I can feel it in my knees.', 12, 0, 100, 0, 0, 0, 62480, 0, 'Zaiyu');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62164, 0, 0, '|TInterface\\Icons\\spell_shaman_earthquake.blp:20|t Гаралон обнаруживает под собой |3-3($n) и начинает применять |cFFFF0000|Hspell:122774|h[Сокрушение]|h|r!', 41, 0, 100, 0, 0, 0, 62534, 0, 'Гаралон to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62164, 1, 0, '|TInterface\\Icons\\ability_druid_ravage.blp:20|t Гаралону не удалось ударить по меньшей мере двух игроков, и он впадает в |cFFFF0000|Hspell:122754|h[неистовство]|h|r!', 41, 0, 100, 0, 0, 0, 66526, 0, 'Гаралон to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62171, 0, 0, 'Нужно перестроиться, пока ша не атаковали снова!', 12, 0, 100, 0, 0, 0, 56982, 0, 'Воин школы Змеи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62171, 1, 0, 'Жрица Пламенное Сердце предала нас! Мы потеряли храм!', 12, 0, 100, 0, 0, 0, 56983, 0, 'Воин школы Змеи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62195, 0, 0, 'Смотрите-ка! Это же Цзи Огненная Лапа и $p!', 14, 0, 100, 66, 0, 0, 61628, 0, 'Шан\'гок to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62195, 1, 0, 'Именно они помогли Корге Крепкой Гриве сбежать из лап Альянса!', 12, 0, 100, 66, 0, 0, 61629, 0, 'Шан\'гок to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62203, 0, 0, '|3-6($C)... approach me...', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62216, 0, 0, 'Я пойду вперед. Увидимся на другой стороне... надеюсь.', 12, 0, 100, 5, 0, 0, 63517, 0, 'Суна Беззвучный Удар to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62233, 0, 0, 'Просто покажи, куда надо идти. Я понесу груз.', 12, 0, 100, 0, 0, 0, 61718, 0, 'Силач Нюцзао to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62270, 0, 0, '|TInterface\\Icons\\ability_warstomp.blp:20|t %s начинает использовать способность |cFFFF0000|Hspell:127480|h[Крушащий удар]|h|r! Убегайте!', 41, 0, 100, 0, 0, 0, 66237, 0, 'Воресс\'талик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 1, 0, 'Эти отрицательные эмоции могут обретать физическую форму. Мы называем их "ша".', 12, 0, 100, 0, 0, 0, 61705, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 2, 0, 'Тебе незнакомы эти земли, а значит ты ничего не знаешь и о Шадо-Пан.', 12, 0, 100, 0, 0, 0, 61702, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 3, 0, 'Темные силы дремлют в этих землях. Злость, ненависть, страх, сомнение...', 12, 0, 100, 0, 0, 0, 61704, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 4, 0, 'Наш священный долг – находить ша и отправлять их в заточение. Мы должны побеждать их, когда они захватывают сердца нашего народа.', 12, 0, 100, 0, 0, 0, 61706, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 5, 0, 'Нами руководит Тажань Чжу, как до этого руководил его отец. Но он решил закрыть врата монастыря – интересно, зачем?', 12, 0, 100, 0, 0, 0, 61711, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 6, 0, 'Мы должны это узнать. Без Шадо-Пан тьма поглотит Пандарию...', 12, 0, 100, 0, 0, 0, 61710, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 7, 0, 'Я занимаюсь этим уже много лет, но я не помню, чтобы когда-либо ша были так сильны, как после высадки ваших солдат.', 12, 0, 100, 0, 0, 0, 61707, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 8, 0, 'Пандария не держит постоянной армии. Мы, Шадо-Пан, ее первая и последняя линия обороны.', 12, 0, 100, 0, 0, 0, 61708, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62271, 9, 0, 'Мы издавна служим своему народу, еще со времен Шаохао. Он был последним императором Пандарии и основателем Шадо-Пан.', 12, 0, 100, 0, 0, 0, 61703, 0, 'Бань Медвежье Сердце to Воздушный шар Баня');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62301, 0, 0, 'How DARE you question her will!', 12, 0, 100, 0, 0, 0, 61731, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62301, 0, 1, 'I carry out her will! We shall crumble this wall and devour the lands beyond!', 12, 0, 100, 0, 0, 0, 66550, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62301, 0, 2, 'The Klaxxi have betrayed her majesty. Your corpses will be strung from the palace walls for this.', 12, 0, 100, 0, 0, 0, 61729, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62301, 0, 3, 'What\'s this?! Now they come over the wall to oppose us? The fools!', 12, 0, 100, 0, 0, 0, 61732, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62352, 0, 0, 'Loot and pillage, bwahahaha!', 14, 0, 100, 0, 0, 32500, 61953, 0, 'Chief Salyis to Galleon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62366, 0, 0, 'Приближаются всадники!', 14, 0, 100, 0, 0, 0, 63441, 0, 'Шоми to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62410, 0, 0, 'Мы сделали все, что смогли. Прощай, Гао-Жань. Я отправлю подкрепление, как только появится возможность.', 12, 0, 100, 1, 0, 0, 61770, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62412, 0, 0, 'До перекрестка недалеко. Там мы встретим последнего союзника.', 12, 0, 100, 1, 0, 0, 61920, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62436, 0, 0, 'Дальше я справлюсь одна. А ты бери стрелы и иди сражаться на мост!', 12, 0, 100, 1, 0, 0, 61778, 0, 'Таоши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62444, 0, 0, 'Готова, жду тебя.', 12, 0, 100, 396, 0, 0, 61828, 0, 'Таоши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62444, 1, 0, 'Похоже, перед нами стоит трудная задача.', 12, 0, 100, 396, 0, 0, 61827, 0, 'Таоши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62444, 2, 0, 'За баррикады!', 12, 0, 100, 0, 0, 0, 61831, 0, 'Таоши to Норвакесс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62444, 3, 0, 'Укройся за баррикадами!', 12, 0, 100, 0, 0, 0, 61830, 0, 'Таоши to Норвакесс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62444, 4, 0, 'Прекрасно. Вернемся к господину Чжу. Здесь больше нечего делать.', 12, 0, 100, 396, 0, 0, 61829, 0, 'Таоши to Норвакесс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62529, 0, 0, 'Возвращайся к Тажаню Чжу. Я скоро тебя догоню.', 12, 0, 100, 1, 0, 0, 61919, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62529, 1, 0, 'Мы почти победили!', 12, 0, 100, 1, 0, 0, 61917, 0, 'Лао-Чинь Стальное Брюхо to Огромный кор\'тик - раздиратель плоти');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62529, 2, 0, 'Вылей обратно!', 12, 0, 100, 1, 0, 0, 61912, 0, 'Лао-Чинь Стальное Брюхо');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62529, 3, 0, 'Отлично! Я рад, что мы пошли вместе!', 12, 0, 100, 1, 0, 0, 61918, 0, 'Лао-Чинь Стальное Брюхо to Огромный кор\'тик - раздиратель плоти');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62529, 4, 0, 'Вот, выпей. Сразу взбодришься.', 12, 0, 100, 1, 0, 0, 61910, 0, 'Лао-Чинь Стальное Брюхо');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 1, 0, 'Wakener, your deeds have earned you the trust of the Klaxxi.', 12, 0, 100, 0, 0, 30312, 67848, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 2, 0, 'You are to be rewarded. Walk with me.', 12, 0, 100, 0, 0, 30313, 67849, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 3, 0, 'We mantid are an elder race. The pandaren you associate with - they are but children. They have their role to play.', 12, 0, 100, 0, 0, 30314, 67850, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 4, 0, 'Each cycle, our young swarm their walls. The pandaren slay the weak. The strong return.', 12, 0, 100, 0, 0, 30315, 67851, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 5, 0, 'With each generation, we grow ever stronger.', 12, 0, 100, 0, 0, 30316, 67852, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 6, 0, 'Before your history began, our empire was vast. We shared this world with our sister kingdoms, Ahn\'Qiraj and Azjol-Nerub.', 12, 0, 100, 0, 0, 30317, 67853, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 7, 0, 'Our Gods were many, and powerful. We mantid worshipped the seven heads of Y\'shaarj.', 12, 0, 100, 0, 0, 30318, 67854, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 8, 0, 'Great was the Old One, and terrible was His wrath. He consumed hope and begat despair, He inhaled courage and breathed fear.', 12, 0, 100, 0, 0, 30319, 67855, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 9, 0, 'When the usurpers came - the ones you call "Titans" - Y\'shaarj was destroyed.', 12, 0, 100, 0, 0, 30320, 67856, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 10, 0, 'His last terrible breath has haunted this land ever since, but the shadows he left behind are mere whispers of his former glory.', 12, 0, 100, 0, 0, 30321, 67857, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 11, 0, 'I tell you now, because you have earned this warning.', 12, 0, 100, 0, 0, 30322, 67858, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 12, 0, 'Your gods are not our gods, outsider.', 12, 0, 100, 0, 0, 30323, 67859, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 13, 0, 'If the Old Ones ever return, we mantid will once again stand by their side.', 12, 0, 100, 0, 0, 30324, 67860, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62538, 14, 0, 'The wisest among you will do the same.', 12, 0, 100, 0, 0, 30325, 67861, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62541, 0, 1, 'Come, lambs... come and face your demise!', 14, 0, 100, 0, 0, 0, 62004, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62541, 0, 0, 'Give in. The more you fight, the more your hatred broils within you.', 14, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62547, 0, 0, 'Что-то происходит с богомолами... и я подозреваю, что это связано с храмом на той стороне моста.', 12, 0, 100, 0, 0, 0, 62049, 0, 'Тай Хо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62547, 1, 0, 'Я встречу тебя в храме Нюцзао, $p. Наши поиски еще не окончены...', 12, 0, 100, 0, 0, 0, 62050, 0, 'Тай Хо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62550, 0, 0, 'Ha! You think you\'re ready to take me on?', 12, 0, 100, 1, 0, 0, 62603, 0, 'Gossip text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62550, 1, 0, 'Let\'s see if you\'re still standing after this.', 12, 0, 100, 1, 0, 0, 62604, 0, 'Gossip text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62574, 0, 0, 'Его панцирь расколот на спине. Надо бить по больному!', 14, 0, 100, 22, 0, 0, 61958, 0, 'Повелитель ястребов Нужун to Воресс\'талик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62574, 1, 0, 'Только не попади под его удар!', 14, 0, 100, 22, 0, 0, 61963, 0, 'Повелитель ястребов Нужун to Воресс\'талик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62574, 2, 0, 'Лучше атаковать его сзади.', 14, 0, 100, 22, 0, 0, 61959, 0, 'Повелитель ястребов Нужун to Воресс\'талик');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62579, 1, 0, 'Артиллерия выжжет землю, не оставив ничего!', 14, 0, 100, 0, 0, 0, 65687, 0, 'Корвекксис');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62579, 2, 0, 'Я проиграл, но вам не удастся выжить, чтобы поведать о своей победе.', 14, 0, 100, 0, 0, 0, 65686, 0, 'Корвекксис');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62580, 0, 0, 'Я проиграл, но вам не удастся выжить, чтобы поведать о своей победе.', 14, 0, 100, 0, 0, 0, 65686, 0, 'Серевекс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62580, 3, 0, 'Эй, подкрепление, сюда! Убейте этих слабаков!', 14, 0, 100, 0, 0, 0, 65688, 0, 'Серевекс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62581, 0, 0, 'Ха-ха, бескрылые глупцы!', 14, 0, 100, 0, 0, 0, 65689, 0, 'Ротек');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62581, 1, 0, 'Я проиграл, но вам не удастся выжить, чтобы поведать о своей победе.', 14, 0, 100, 0, 0, 0, 65686, 0, 'Ротек');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62581, 2, 0, 'Глупцы, вы умрете вместе со мной!', 14, 0, 100, 0, 0, 0, 65685, 0, 'Ротек');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62593, 0, 0, 'Хорошо.', 12, 0, 100, 0, 0, 0, 140215, 0, 'Таоши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62593, 1, 0, 'Выступай. Я пойду следом.', 12, 0, 100, 0, 0, 0, 62043, 0, 'Таоши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62595, 0, 0, 'Неплохо... неплохо...', 12, 0, 100, 0, 0, 0, 62048, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62600, 0, 0, 'Впечатляет!', 12, 0, 100, 0, 0, 0, 75251, 0, 'Повелитель ястребов Нужун to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62707, 0, 0, 'Враг не проявит милосердие. Поступай с ним так же.', 12, 0, 100, 1, 0, 0, 62219, 0, 'Шоми');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62730, 0, 0, 'Господин Чжу, мы скоро вернемся.', 12, 0, 100, 1, 0, 0, 61905, 0, 'Лао-Чинь Стальное Брюхо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62735, 0, 0, 'I hope your moves aren\'t too predictable.', 12, 0, 100, 1, 0, 0, 62637, 0, 'Gossip text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62736, 0, 0, 'Полагаю, на мосту вы одержали победу?', 12, 0, 100, 6, 0, 0, 61927, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62773, 0, 0, 'Freedom...', 12, 0, 100, 0, 0, 0, 62574, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62773, 1, 0, 'For eight hundred and seventy-seven years, I waited in that crystal... watching shadows pass through the cracks, counting decades.', 12, 0, 100, 0, 0, 0, 62575, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62773, 2, 0, 'I am needed elsewhere.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62774, 1, 0, 'Who holds this swarm? Show yourself! Answer to the Klaxxi.', 12, 0, 100, 0, 0, 0, 67837, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62774, 2, 0, 'Speak your name puppet, that our annals will be complete.', 12, 0, 100, 0, 0, 0, 67839, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62774, 3, 0, 'Your "Empress" has fallen. The Klaxxi have pronounced judgment. Stand aide or be brushed away.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62778, 0, 0, 'NO... YOU WILL NOT KEEP ME INSIDE THIS ABHORRENT PRISON...', 12, 0, 100, 0, 0, 0, 68787, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62778, 1, 0, 'SET ME FREE... THE KLAXXI CALL...', 12, 0, 100, 0, 0, 0, 68785, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62778, 2, 0, 'THE HATCHING HOUR IS NIGH... I MUST ANSWER TO THE SWARM...', 12, 0, 100, 0, 0, 0, 68786, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62778, 3, 0, 'I... wah... where? What is going on?', 12, 0, 100, 0, 0, 0, 62567, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62778, 4, 0, 'Oh, that. Bah, I have been through worse. And who exactly are you?', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62778, 5, 0, 'STORMSTOUT? No. You\'re foolin\'. Really? A Wandering Isle Stormstout, here, on Pandaria! Where\'s the rest of the family?', 12, 0, 100, 0, 0, 0, 62571, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62781, 0, 0, 'I accept your challenge!', 12, 0, 100, 0, 0, 0, 62298, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62782, 0, 0, 'Are you really as strong as the others say?', 12, 0, 100, 0, 0, 0, 62247, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62786, 0, 0, 'Я рад сражаться рядом с тобой!', 12, 0, 100, 66, 0, 0, 61969, 0, 'Повелитель ястребов Нужун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62788, 0, 0, 'Если господин Чжу хочет нас видеть, лучше не заставлять его ждать. Идем.', 12, 0, 100, 1, 0, 0, 61970, 0, 'Повелитель ястребов Нужун to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62802, 0, 0, 'Без тебя у нас ничего бы не вышло, $p. Спасибо!', 12, 0, 100, 396, 0, 0, 62042, 0, 'Таоши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62803, 4, 0, 'Настал тот час, ради которого вы тренировались всю свою жизнь!', 14, 0, 100, 0, 0, 0, 62029, 0, 'Тажань Чжу to Ша Ненависти');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 0, 0, 'Вот так и сбываются мечты?', 12, 0, 100, 3, 0, 0, 63963, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 1, 0, 'Твой як в другом стойле.', 12, 0, 100, 3, 0, 0, 63952, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 2, 0, 'Я езжу на яках. А это мой як.', 12, 0, 100, 3, 0, 0, 63973, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 3, 0, 'Мне хватит и минуты. Нет, подожди, не то.', 12, 0, 100, 3, 0, 0, 63978, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 4, 0, 'Я прокачусь через ворота Могу\'шан.', 12, 0, 100, 3, 0, 0, 63977, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 5, 0, 'Ничто во всей Пандарии не выстоит против буйной ярости большого яка для путешествий. Молись, чтобы никогда не увидеть ее.', 12, 0, 100, 3, 0, 0, 63974, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 6, 0, 'Овса не найдется?', 12, 0, 100, 3, 0, 0, 63948, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 7, 0, 'У этих яунголов прямые мечи. Прямые. Мечи.', 12, 0, 100, 3, 0, 0, 63971, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 8, 0, 'Как будто от меня часть отгрызли.', 12, 0, 100, 3, 0, 0, 63989, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 9, 0, 'Яки. Яки никогда не меняются.', 12, 0, 100, 3, 0, 0, 63951, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 10, 0, 'Проснись и пой.', 12, 0, 100, 3, 0, 0, 63949, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 11, 0, 'Я вовсе не собираюсь с тобой судиться.', 12, 0, 100, 3, 0, 0, 63975, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 12, 0, 'О горе. Ниже, ниже.', 12, 0, 100, 3, 0, 0, 63972, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62821, 13, 0, 'По-моему, як куда-то пошел.', 12, 0, 100, 3, 0, 0, 63970, 0, 'Мистик Птицекров to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 0, 0, 'Не покажешь ли ты мне дорогу в Кунь-Лай?', 12, 0, 100, 6, 0, 0, 63954, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 1, 0, 'Десять лет. Я преданно чищу яков целых десять лет.', 12, 0, 100, 6, 0, 0, 63965, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 2, 0, 'Помогает ли яку хвост удерживать равновесие?', 12, 0, 100, 6, 0, 0, 63976, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 3, 0, 'Надо обновить журнал.', 12, 0, 100, 6, 0, 0, 63987, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 4, 0, 'Да я сюда за репой пришел, честное слово!', 12, 0, 100, 6, 0, 0, 63967, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 5, 0, 'Может, ты покатишь бочку?', 12, 0, 100, 6, 0, 0, 63955, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 6, 0, 'Я хотел устроить праздник и позвать Мистика Птицекрова, но из-за тебя он ушел.', 12, 0, 100, 6, 0, 0, 63969, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 7, 0, 'Я жалкое существо из мяса и пива.', 12, 0, 100, 6, 0, 0, 63957, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 8, 0, 'Я чувствую себя фаршем.', 12, 0, 100, 6, 0, 0, 63988, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 9, 0, 'Когда споришь с яком, есть две теории. Обе не работают.', 12, 0, 100, 6, 0, 0, 63966, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 10, 0, 'Жалко класть свой зад на такую красоту.', 12, 0, 100, 6, 0, 0, 63961, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 11, 0, 'Не опасно ли идти в одиночку?', 12, 0, 100, 6, 0, 0, 63953, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62822, 12, 0, 'Чем больше положишь, тем больше войдет.', 12, 0, 100, 6, 0, 0, 63959, 0, 'Братец Вялые Руки to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62825, 0, 0, 'Are you ready?', 12, 0, 100, 0, 0, 0, 105292, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62849, 0, 0, '%s растворяется в тенях.', 16, 0, 100, 0, 0, 0, 68774, 0, 'Луч солнца');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62881, 0, 0, '|TInterface\\Icons\\ability_warlock_baneofhavoc:20|t%s начинает произносить |cFFFF0000|Hspell:126299|h[Освобождение душ]|h|r! Убегайте!', 41, 0, 100, 0, 0, 0, 65223, 0, 'Гаохунь Ловец Душ');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62970, 0, 0, 'Вечно ты недоволен, Хоу!', 12, 0, 100, 0, 0, 0, 63294, 0, 'Старейшина Чи to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62971, 0, 0, 'Обещания, обещания! Ты никак не сможешь нам помочь. Зандалары не ведают жалости.', 12, 0, 100, 5, 0, 0, 63286, 0, 'Старейшина Хоу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62978, 0, 0, 'A challenge, huh? Let\'s see what you\'ve got.', 12, 0, 100, 1, 0, 0, 62636, 0, 'Gossip text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62978, 1, 0, 'Oh, yeah. I remember you. Let\'s see what you\'ve learned.', 12, 0, 100, 1, 0, 0, 62635, 0, 'Gossip text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (62996, 0, 0, 'Добро пожаловать, $gпутник:путница,. Располагайся тут, если хочешь. Наверху ты найдешь банк и городские порталы. По всему зданию – рынок. Не стесняйся, ходи и смотри.', 12, 0, 100, 0, 0, 0, 66597, 0, 'Мадам Ви Ло to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63025, 0, 0, 'Нет... священные воды... надо сопротивляться. Я не должен бояться...', 14, 0, 100, 0, 0, 0, 63272, 0, 'Цулон to Защитник Каолань');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63053, 0, 0, '|TInterface\\Icons\\spell_nature_healingwavelesser.blp:20|t Garalon |cFFFF0000|Hspell:123495|h[Mends]|h|r one of his legs!', 41, 0, 100, 0, 0, 0, 62837, 0, 'Garalon\'s Leg to Garalon');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63053, 1, 0, '|TInterface\\Icons\\spell_nature_healingwavelesser.blp:20|t Гаралон |cFFFF0000|Hspell:123495|h[восстанавливает]|h|r себе одну ногу!', 41, 0, 100, 0, 0, 0, 62837, 0, 'Нога Гаралона to Гаралон');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63071, 0, 0, 'Das ist das Ende unserer Unterhaltung.', 12, 0, 100, 396, 0, 0, 66303, 0, 'Skeer der Blutsucher to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (63071, 1, 0, 'Was war der letzte Teil?', 12, 0, 100, 6, 0, 0, 66299, 0, 'Skeer der Blutsucher to Player');
