INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65638, 5, 0, 'Твоему народу суждено подчиниться.', 12, 0, 100, 0, 0, 0, 57194, 0, 'Надсмотрщик из Корцзя to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65647, 0, 0, 'Did the Order of the Cloud Serpent send you? Bless them. Please help the others!', 12, 0, 100, 1, 0, 0, 66832, 0, 'ResqueText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65648, 0, 0, 'Когда-то у меня была своя ферма, но с тех пор уже столько лет прошло. Мне нравятся заварушки, а тебе?', 12, 0, 100, 0, 0, 0, 66845, 0, 'Старик Макдональд to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65651, 0, 0, 'Мне удалось поймать только пару кроликов. Зато теперь у меня их много!', 12, 0, 100, 0, 0, 0, 66836, 0, 'Линдси to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65714, 0, 0, 'Идем, я покажу.', 12, 0, 100, 66, 0, 0, 61979, 0, 'Таоши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65746, 0, 0, 'Осторожнее, Цзюнь-Цзюнь! Тебя может унести в море мощным порывом ветра!', 12, 0, 100, 1, 0, 0, 67135, 0, 'Тэнфэй Прибрежная Вода');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65808, 0, 0, 'Каждая травма – это своеобразный жизненный урок. Каждое поражение – очередная ступень к совершенству.', 42, 0, 100, 0, 0, 0, 66941, 0, 'Святилище тела Искателя to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65811, 0, 0, 'Когда ты обретешь внутренний покой, то сможешь ясно видеть все препятствия в своем сознании.', 42, 0, 100, 0, 0, 0, 66946, 0, 'Святилище дыхания Искателя to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65812, 0, 0, 'Мудрость – это дар, который дается только тем, кто готов его принять.', 42, 0, 100, 0, 0, 0, 66948, 0, 'Святилище сердца Искателя to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 0, 'I was relieving myself into one of the golden pools when they caught me. I guess you could call it a karma capture.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 1, 'I\'m getting rusty... and my lockpicks are too apparently. Do me a favor, don\'t let Ren know you found me here, kay?', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 2, 'I\'ve made a mental note, mogu are light sleepers. Pass the word.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 3, 'It feels so good to stretch my limbs! It was cramped in there. They really should invest in more roomy cages!', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 4, 'Ren bet me that I couldn\'t steal one of the mogu cannons. I didn\'t think they\'d be so heavy!', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 5, 'Thanks!', 12, 0, 100, 0, 0, 0, 112662, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 6, 'That\'s the last time I\'ll let them catch me, promise.', 12, 0, 100, 0, 0, 0, 67058, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 7, 'What took you so long? I almost thought they weren\'t going to send someone for me this time... my heart frowned a little.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65868, 0, 8, 'You know, I\'ve been stuck in these cages watching the mogu a lot, but I\'m pretty sure I\'ve never seen a girl mogu. What\'s with that?', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65870, 0, 0, 'Догоняй, черепаха!', 12, 0, 100, 0, 0, 0, 67040, 0, 'Мэй Черная Бровь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65871, 0, 0, 'Сейчас я тебя поймаю!', 12, 0, 100, 0, 0, 0, 67035, 0, 'Ло Черная Бровь');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65896, 0, 0, 'Делай, что хочешь. Придумываешь себе вопросы, на которые нет ответа!', 12, 0, 100, 0, 0, 0, 67133, 0, 'Жительница деревни Цзоучин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65896, 1, 0, 'Что за буквы? На крышке? Наверное, это чьи-то инициалы. Какая-нибудь ерунда.', 12, 0, 100, 0, 0, 0, 67128, 0, 'Жительница деревни Цзоучин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65896, 2, 0, 'Что за чушь? Обычная урна с жуками. По мне, так она вообще отвратительная.', 12, 0, 100, 0, 0, 0, 67130, 0, 'Жительница деревни Цзоучин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65896, 3, 0, 'Бред какой-то! Не стоило тебе покидать деревню!', 12, 0, 100, 0, 0, 0, 67131, 0, 'Жительница деревни Цзоучин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65897, 0, 0, 'Должно быть, это послание! Может быть, ключ к великой тайне!', 12, 0, 100, 1, 0, 0, 67126, 0, 'Житель деревни Цзоучин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65897, 1, 0, 'Ты не понимаешь! Это же может быть сигналом конца света!', 12, 0, 100, 6, 0, 0, 67127, 0, 'Житель деревни Цзоучин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65897, 2, 0, 'Я нашел эту урну с жуками в лачуге посреди Кунь-Лай. Вначале решил, что это хлам, пока не приметил на ней какие-то буквы...', 12, 0, 100, 6, 0, 0, 67125, 0, 'Житель деревни Цзоучин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65897, 3, 0, 'Попробую-ка я разгадать эту загадку. Посмотрим, куда меня приведет эта надпись.', 12, 0, 100, 273, 0, 0, 67134, 0, 'Житель деревни Цзоучин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65911, 0, 0, 'Мишка о тебе позаботится, слышишь?', 12, 0, 100, 0, 0, 0, 67779, 0, 'Лейтенант Даниэль');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65911, 1, 0, 'Следующий десяток орков я убью в твою честь.', 12, 0, 100, 0, 0, 0, 67780, 0, 'Лейтенант Даниэль');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65911, 2, 0, 'Пульс ровный, Мишка, но очень слабый.', 12, 0, 100, 0, 0, 0, 67776, 0, 'Лейтенант Даниэль');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65911, 3, 0, 'Держись, приятель! Все будет хорошо.', 12, 0, 100, 0, 0, 0, 67775, 0, 'Лейтенант Даниэль');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65928, 0, 0, 'You dare challenge the yaungol?', 12, 0, 100, 0, 0, 0, 64550, 0, 'Ordo Raider to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65928, 1, 0, 'Сдавайся!', 12, 0, 100, 0, 0, 0, 94374, 0, 'Рейдер Ордо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65928, 2, 0, 'Кто смеет бросать вызов яунголам?', 12, 0, 100, 0, 0, 0, 64550, 0, 'Рейдер Ордо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65928, 3, 0, 'Теперь это наши поля.', 12, 0, 100, 0, 0, 0, 64552, 0, 'Рейдер Ордо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65928, 4, 0, 'Еще одним рабом больше!', 12, 0, 100, 0, 0, 0, 64549, 0, 'Рейдер Ордо to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65974, 0, 0, 'Can\'t wait to get back home.', 12, 0, 0, 1, 0, 0, 68660, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65974, 0, 1, 'I suppose it is time I headed home.', 12, 0, 0, 1, 0, 0, 68653, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65974, 0, 2, 'Thanks! I\'m outta here!', 12, 0, 0, 1, 0, 0, 68658, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65974, 0, 3, 'Many thanks.', 12, 0, 0, 1, 0, 0, 68661, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65974, 0, 4, 'Thank goodness!', 12, 0, 0, 1, 0, 0, 68657, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65974, 0, 5, 'Thank you, $r', 12, 0, 0, 1, 0, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (65999, 0, 0, 'Их предводитель все еще прячется. Разыщите его!', 12, 0, 100, 0, 0, 0, 67750, 0, 'Генерал Назгрим to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66052, 0, 0, 'Don\'t you beasts know when to give up?', 12, 0, 100, 0, 0, 0, 67251, 0, 'Captain Doren to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66166, 0, 0, 'No. You\'re lying! My father will protect me!', 12, 0, 100, 0, 0, 0, 49337, 0, 'Lilian Voss');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66209, 0, 0, 'Thanks my friend i\'ll be always in doubt to you.', 12, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66218, 0, 0, 'Нужно отнести хмель в безопасное место. Мое варево поддерживает дух всех горожан.', 12, 0, 100, 0, 0, 0, 67419, 0, 'Ло Донь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66220, 0, 0, 'Should we allow them to stay here? They have wounded to tend to.', 12, 0, 100, 0, 0, 0, 68135, 0, 'Мэр Медовая Роса to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66223, 0, 0, 'They will bring more destruction to our village!', 12, 0, 100, 0, 0, 0, 68137, 0, 'Старейшина Медовая Лапа  to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66223, 1, 0, 'Well what about the bombs that landed in our farms from your warship!', 12, 0, 100, 0, 0, 0, 68136, 0, 'Старейшина Медовая Лапа  to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66223, 2, 0, 'A war we did not have until you arrived!', 12, 0, 100, 0, 0, 0, 68138, 0, 'Старейшина Медовая Лапа  to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66261, 0, 0, 'Покажите им всю мощь троллей!', 14, 0, 100, 5, 0, 0, 67397, 0, 'Зандалар-защитник');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66261, 1, 0, 'Разорвите их на куски!', 14, 0, 100, 0, 0, 0, 67396, 0, 'Зандалар-защитник');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66261, 2, 0, 'Окружайте их! Не дайте им уйти!', 14, 0, 100, 5, 0, 0, 67395, 0, 'Зандалар-защитник');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66261, 3, 0, 'Прикончите их побыстрее! У нас много дел.', 14, 0, 100, 0, 0, 0, 67398, 0, 'Зандалар-защитник');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66267, 0, 0, '$gНичтожный:Ничтожная:r, |3-6($r)...', 12, 0, 100, 0, 0, 0, 67567, 0, 'Гатрул\'лон - насылатель проклятий to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66269, 0, 0, 'Ты труп, |3-6($r)!', 12, 0, 100, 0, 0, 0, 73020, 0, 'Рубака Двух Башен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66273, 0, 0, 'Пандария будет нашей!', 12, 0, 100, 0, 0, 0, 69203, 0, 'Подрывник Двух Башен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66273, 1, 0, 'За Орду!', 12, 0, 100, 0, 0, 0, 143151, 0, 'Подрывник Двух Башен to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66274, 0, 0, 'Армия Адского Крика зальет тут все кровью!', 14, 0, 100, 0, 0, 0, 67574, 0, 'Далган to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66275, 0, 0, 'Я тебя не боюсь, |3-6($r). А вот тебе следовало бы меня опасаться...', 14, 0, 100, 0, 0, 0, 67576, 0, 'Белландра Буря Скверны to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66276, 0, 2, 'This land will be your grave.', 12, 0, 100, 0, 0, 0, 67498, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66276, 0, 0, 'Alliance "hero." Let\'s see how you fight without a flying warship to cover your back!', 12, 0, 100, 0, 0, 0, 67494, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66276, 0, 1, 'They defied us - they defied Hellscream\'s Horde - I should\'ve executed the whole town!', 12, 0, 100, 0, 0, 0, 67496, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66276, 1, 0, 'There\'s something about this land - you don\'t understand...', 14, 0, 100, 0, 0, 0, 67495, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66276, 1, 1, 'Burn, Alliance scum! BURN!', 14, 0, 100, 0, 0, 0, 67497, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66282, 0, 0, 'Lokand gez golgon ', 12, 1, 100, 0, 0, 0, 0, 0, 'Рубака лагеря Гаррош\'ар to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66283, 0, 0, 'You are late scumm !!', 12, 0, 100, 15, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66283, 1, 0, 'This is only the beginning...ha ha ha!!', 12, 0, 100, 11, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66292, 0, 0, 'Спасибо за гостеприимство. Уверяю вас, вы все делаете правильно.', 12, 0, 100, 2, 0, 32549, 67711, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66296, 0, 0, 'Если вы пришли сюда с войной, то сама земля ответит на вашу жестокость и ваши страсти. И я не могу вам сказать, какими будут последствия. Никто этого не знает.', 12, 0, 100, 25, 0, 32577, 68790, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66300, 0, 0, 'Задай им трепку, $n! Кем бы они ни были...', 12, 0, 100, 0, 0, 0, 67651, 0, 'Пехотинец "Небесного огня" to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66321, 0, 0, 'УЗРИТЕ МОЮ СИЛУ!', 14, 0, 100, 0, 0, 32684, 110374, 0, 'Ксу\'даггаб to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66349, 0, 0, '%s полностью исчезает в пламени, а в его взгляде появляется безумие!', 16, 0, 100, 0, 0, 0, 10425, 0, 'Летучий всадник Гурубаши');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66352, 0, 0, 'Глупая змея! Стреляй, говорю!', 12, 0, 100, 0, 0, 0, 67515, 0, 'Предатель Глак');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66369, 0, 0, 'Только без паники, Тейлор. Я оставляю с вами агентов ШРУ. Проведите разведывательную операцию.', 12, 0, 100, 11, 0, 32557, 67506, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66369, 1, 0, 'Боюсь, вам придется пробиваться туда без нас.', 12, 0, 100, 1, 0, 32555, 67504, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66369, 2, 0, 'У меня приказ обеспечить основной группе поддержку с воздуха. Через два месяца все силы флота Альянса будут у южных берегов.', 12, 0, 100, 1, 0, 32556, 67505, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66369, 3, 0, 'Адмирал! Приятно видеть, что с вами все в порядке.', 12, 0, 100, 3, 0, 32554, 67503, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66369, 4, 0, 'Может быть, вам удастся завербовать кого-то из местных. Уверена, вы справитесь.', 12, 0, 100, 1, 0, 32558, 67507, 0, 'Небесный адмирал Роджерс to Личный гирокоптер адмирала Роджерс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66372, 0, 0, 'Эта земля многое пережила, но ей удалось оправиться от разрушений, который принес разрушитель миров. Сейчас ты увидишь, что мои спутники такие же стойкие, как и сама земля.', 12, 0, 100, 0, 0, 0, 67516, 0, 'Мерда Могучее Копыто to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66379, 0, 0, 'Спасибо, Кэтрин. Хорошо, что ты здесь. С поддержкой авианосца мы сможем пробиться к северо...', 12, 0, 100, 1, 0, 32436, 67508, 0, 'Адмирал Тейлор to Небесный адмирал Роджерс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66379, 1, 0, 'Что?', 12, 0, 100, 6, 0, 32437, 127498, 0, 'Адмирал Тейлор to Небесный адмирал Роджерс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66379, 2, 0, 'И что мне до тех пор делать? У меня нет людей! Мы не знаем, где сейчас силы Орды и сколько их!', 12, 0, 100, 5, 0, 32438, 67510, 0, 'Адмирал Тейлор to Небесный адмирал Роджерс');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66406, 0, 0, 'You are late, i left some gift for you ahahaha', 12, 0, 100, 0, 0, 0, 0, 0, 'Gathrul to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66406, 1, 0, 'We shall meet soon to end this, come and find me!', 12, 0, 100, 0, 0, 0, 0, 0, 'Gathrul to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66407, 0, 0, 'Чернокнижник! Он не мог далеко уйти – где-то тут должна быть еще одна база.', 12, 0, 100, 1, 0, 32666, 67593, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66422, 0, 0, 'Hey there $Ghot stuff:toots,, I\'m all revved up and ready to battle!', 12, 0, 100, 0, 0, 0, 68513, 0, 'Cassandra Kaboom to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66466, 0, 0, 'Как дела, золотце?', 12, 0, 100, 0, 0, 0, 67641, 0, 'Ледяная Трикси to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66501, 0, 0, 'Молодец, $n! Отправляйся в деревню за мостом. Я прикрою отход пехотинцев.', 12, 0, 100, 0, 0, 0, 67661, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66581, 0, 0, 'Я не удивлен. Орда – это просто чудовища!', 12, 0, 100, 1, 0, 32677, 67707, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66581, 1, 0, 'Если оставить все как есть, Орда захватит эту землю. Вы не поможете нам сражаться с ними?', 12, 0, 100, 1, 0, 32678, 67709, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66581, 2, 0, 'Мы здесь с мирными целями. Вы поможете нам?', 12, 0, 100, 6, 0, 0, 68485, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66582, 0, 0, 'Но в этой кровавой бойне мы участвовать не будем.', 12, 0, 100, 274, 0, 0, 67819, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66582, 1, 0, 'Я видел вашу небесную крепость. У вашего народа очень странные представления о "мире".', 12, 0, 100, 1, 0, 32569, 67708, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66582, 2, 0, 'Ваша вражда будет иметь колоссальные последствия в этих землях. Не смейте осквернять их войной!', 12, 0, 100, 5, 0, 0, 67820, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66582, 3, 0, 'Твоего врага подчинила себе темная энергия, которую мы называем "ша". Ша черпают силы в отрицательных эмоциях, например, сомнениях.', 12, 0, 100, 1, 0, 32711, 67706, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66582, 4, 0, 'Сражений мы не боимся. Хоть наших воинов и немного, они очень сильны и опытны.', 12, 0, 100, 1, 0, 32570, 67757, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66582, 5, 0, 'Но вот уже много лет я не видел, чтобы ша были такими сильными.', 12, 0, 100, 25, 0, 0, 68486, 0, 'Тажань Чжу to Релл Ночной Ветер');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66583, 0, 0, 'Их война – это не просто расовая междоусобица. Их ненависть затопит нашу землю. Мы не будем принимать в этом участия.', 12, 0, 100, 1, 0, 32566, 67713, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66583, 1, 0, 'Ваши солдаты могут расположиться здесь. Но мы не переходим ни на чью сторону.', 12, 0, 100, 25, 0, 32565, 67712, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66583, 2, 0, 'Мне пора. Еще кто-то высадился на наших северных берегах.', 12, 0, 100, 1, 0, 32567, 67714, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66633, 0, 0, '|TInterface\\Icons\\ability_druid_tigersroar.blp:20|t %s прячется и готовиться к |cFFFF0000|Hspell:131271|h[прыжку из теней]|h|r!', 41, 0, 100, 0, 0, 0, 67913, 0, 'Руукти');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66643, 0, 0, 'What is this... shadow? What is the Alliance playing at?', 12, 0, 100, 6, 0, 0, 67752, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66643, 1, 0, 'You finished him - well done!', 12, 0, 100, 5, 0, 0, 67751, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66643, 2, 0, 'You again. Do you see it now? Alliance arrogance!', 12, 0, 100, 1, 0, 0, 67754, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66643, 3, 0, 'Help us rout them from your land, and your kind can stand tall and proud with the Horde!', 12, 0, 100, 1, 0, 0, 68334, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66643, 4, 0, 'Are you afraid to fight?', 12, 0, 100, 6, 0, 0, 67756, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66643, 5, 0, 'This is a hostile land, and we have no reinforcements. We\'re going to have to improvise, champion.', 12, 0, 100, 1, 0, 0, 67758, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66643, 6, 0, 'But our orders stand - Hellscream will have his foothold here. We will NOT allow the Alliance to lay claim to this new world!', 12, 0, 100, 5, 0, 0, 67759, 0, 'General Nazgrim to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66644, 0, 0, 'Your foe was overwhelmed by a dark energy we call the"Sha." It thrives on negative emotions, such as doubt.', 12, 0, 100, 6, 0, 0, 67753, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66644, 1, 0, 'It has been many years since I have seen it manifest so dramatically.', 12, 0, 100, 1, 0, 0, 68486, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66644, 2, 0, 'My people have stood tall without you for thousands of years.', 12, 0, 100, 25, 0, 0, 67755, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66644, 3, 0, 'We are not afraid to fight. Our warriors, while few in number, are extremely skilled.', 12, 0, 100, 1, 0, 0, 67757, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66644, 4, 0, 'But we will not participate in this... race war... bloodbath of yours.', 12, 0, 100, 0, 0, 0, 67819, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66644, 5, 0, 'Your conflict will have immense consequences here. Do NOT bring your war to these shores!', 12, 0, 100, 0, 0, 0, 67820, 0, 'Тажань Чжу to 0');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66655, 0, 0, 'Тут жить гну-сини!', 12, 0, 100, 0, 0, 0, 54876, 0, 'Кривоскок-соня to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66666, 0, 0, 'К нам в деревню пожаловал еще один гость... Судя по виду, это |3-6($c). Проходи, но только не направляй на нас свое оружие, как это сделала Орда.', 12, 0, 100, 0, 0, 0, 68561, 0, 'Защитник Лап\'дон to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66693, 0, 0, 'Держите свои блохастые лапы подальше от еды, никчемные животные!', 14, 0, 100, 5, 0, 0, 67813, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66693, 1, 0, 'Только притроньтесь к припасам, и я вам головы оторву!', 14, 0, 100, 5, 0, 0, 67811, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66693, 2, 0, 'Работайте поживее, ленивые свиньи, или я вас сотру в порошок!', 14, 0, 100, 5, 0, 0, 67810, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66693, 3, 0, 'Сторожите припасы во все глаза, недоноски!', 14, 0, 100, 5, 0, 0, 67812, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66693, 4, 0, 'Внимательно следите за наемниками, братья!', 14, 0, 100, 5, 0, 0, 67814, 0, 'Зандаларский предводитель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66700, 0, 0, 'Защищайте руины любой ценой!', 14, 0, 100, 5, 0, 0, 67823, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66700, 1, 0, 'Переверните каждый камень!', 14, 0, 100, 5, 0, 0, 67824, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66700, 2, 0, 'Не подведите меня! Вскоре мы воссоединимся с древними союзниками-могу!', 14, 0, 100, 5, 0, 0, 67825, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66700, 3, 0, 'В этом месте покоится скрытая сила. Мы должны найти ее!', 14, 0, 100, 5, 0, 0, 67821, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66700, 4, 0, 'Я чувствую присутствие духов давно усопших могу!', 14, 0, 100, 5, 0, 0, 67822, 0, 'Зандаларский прорицатель');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66731, 0, 0, 'Вот это да.', 12, 0, 100, 0, 0, 0, 76670, 0, 'Мистер Кризи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66731, 1, 0, 'Вы попали прямо в расставленный мной капкан.', 12, 0, 100, 0, 0, 0, 76674, 0, 'Мистер Кризи');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66832, 0, 0, 'Пойдем со мной, $n. Я хочу нанести визит крестьянину Юну.', 12, 0, 100, 0, 0, 0, 0, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66832, 1, 0, 'Тебе нужно получить еще четыре голоса. Но это вопрос времени.', 12, 0, 100, 0, 0, 0, 67922, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66832, 2, 0, 'Если хочешь научиться лучше готовить, поговори с кем-нибудь из семейства Железная Лапа.', 12, 0, 100, 0, 0, 0, 68218, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66832, 3, 0, 'Знаешь, я обязана тебе своей шкурой за этот лук-шалот.', 12, 0, 100, 0, 0, 0, 67918, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66832, 4, 0, 'Послушайте, раз уж вы так замечательно помогли мне, я отдам свой голос за то, чтобы вас приняли в земледельцы.', 12, 0, 100, 2, 0, 0, 67921, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66832, 5, 0, 'Эй, фермер Юн!', 12, 0, 100, 3, 0, 0, 67920, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66832, 6, 0, 'Обустраивайся и приходи на рынок в любое время, если тебе понадобится работа.', 12, 0, 100, 1, 0, 0, 67923, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66832, 7, 0, 'Добро пожаловать в Плодородные земли!', 12, 0, 100, 3, 0, 0, 67925, 0, 'Джина Грязный Коготь to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66845, 0, 0, 'How can you remain neutral after watching them blow us out of the sky?!', 12, 0, 100, 0, 0, 0, 68139, 0, ' Сержант Горрок to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66845, 1, 0, 'Those are the casualties of war!', 12, 0, 100, 0, 0, 0, 68140, 0, ' Сержант Горрок to player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66905, 0, 0, 'Сюда, быстрее! Пушка пробила путь во двор!', 12, 0, 100, 1, 0, 29068, 68130, 0, 'Пандарен-артиллерист');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66949, 0, 0, 'Дипломатия уже сыграла свою роль. Теперь мы сможем доказать свои намерения только делами.', 12, 0, 100, 0, 0, 0, 61160, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66978, 0, 0, 'Ребята, вас готовили к этому. Вы – элита! Вы – команда "Небесного огня"!', 12, 0, 100, 0, 0, 32550, 68303, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (66978, 1, 0, 'На этом корабле преследовали и сбили Смертокрыла. Думаете, у аванпоста Орды есть хоть какие-то шансы выстоять перед гордостью флота Альянса? Никаких!', 12, 0, 100, 0, 0, 32551, 68304, 0, 'Небесный адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67012, 0, 0, 'Лучше не говорить об этом здесь. Если не вдаваться в подробности, это воплощение твоих собственных сомнений.', 12, 0, 100, 1, 0, 32563, 67742, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67012, 1, 0, 'О, я все отлично понимаю. У меня же есть глаза. Но Пандария не похожа на те земли, откуда вы пришли. Она живая. Она дышит. Вы должны думать о том, какую энергию приносите сюда. А теперь уберите оружие!', 12, 0, 100, 1, 0, 32564, 67744, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67048, 0, 0, 'Нет, адмирал. Никаких следов ни капитана Тейлора, ни его корабля.', 12, 0, 100, 0, 0, 32532, 68015, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67048, 1, 0, 'Выпустить торпеды по кораблям.', 12, 0, 100, 0, 0, 32530, 68010, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67048, 2, 0, 'Цель уничтожена. Отличный выстрел.', 12, 0, 100, 0, 0, 32531, 68011, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67048, 3, 0, 'Адмирал, по многим признакам, этот берег обитаем. Богато украшенные строения...', 12, 0, 100, 0, 0, 32528, 68006, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67048, 4, 0, 'Вы слышали приказ. Сосредоточить огонь на базе Орды. Начинайте заход для атаки.', 12, 0, 100, 0, 0, 32529, 68008, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67050, 0, 0, 'Это что за?..', 12, 0, 100, 0, 0, 27840, 68005, 0, 'Салли "Рассольный" Маклири to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67051, 0, 0, 'Отставить разговоры. Все внимание на цель!', 12, 0, 100, 0, 0, 32539, 68007, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67051, 1, 0, 'Отомстите им! Не жалейте боеприпасов!', 12, 0, 100, 0, 0, 32543, 68019, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67051, 2, 0, 'Видно ли флагманский корабль?', 12, 0, 100, 0, 0, 32542, 68014, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67051, 3, 0, 'Отлично. Никого не щадить!', 12, 0, 100, 0, 0, 32540, 68012, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67051, 4, 0, 'Задача выполнена. Всем вернуться. Приготовиться к наземному штурму.', 12, 0, 100, 0, 0, 32789, 68020, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67051, 5, 0, 'Это за Терамор!', 12, 0, 100, 0, 0, 32541, 68013, 0, 'Адмирал Роджерс to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67053, 0, 0, '$n использовал ветряную бомбу!', 16, 0, 100, 0, 0, 30337, 68820, 0, 'Ветряная бомба to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67069, 0, 0, 'Уничтожить...', 14, 0, 100, 0, 0, 0, 68518, 0, 'Ксу\'даггаб to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67090, 0, 0, 'Can\'t wait to get back home.', 12, 0, 0, 1, 0, 0, 68660, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67090, 0, 1, 'I suppose it is time I headed home.', 12, 0, 0, 1, 0, 0, 68653, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67090, 0, 2, 'Thanks! I\'m outta here!', 12, 0, 0, 1, 0, 0, 68658, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67090, 0, 3, 'Many thanks.', 12, 0, 0, 1, 0, 0, 68661, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67090, 0, 4, 'Thank goodness!', 12, 0, 0, 1, 0, 0, 68657, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67090, 0, 5, 'Thank you, $r', 12, 0, 0, 1, 0, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 0, 0, 'Я не знаю, почему расступились туманы. Но я знаю, что на это есть причина.', 12, 0, 100, 0, 0, 28564, 63659, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 1, 0, 'Я вижу, что ты здесь не для того, чтобы принести свою войну в эти земли. Тьма сокрыта в Пандарии, и теперь ты тоже знаешь об этом.', 12, 0, 100, 0, 0, 28565, 63660, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 2, 0, 'Эта битва обошлась мне слишком дорого. Я смогу возродиться лишь через несколько лет.', 12, 0, 100, 0, 0, 28562, 63657, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 3, 0, 'В Долине Четырех Ветров ты многое сможешь узнать об этой земле.', 12, 0, 100, 0, 0, 28567, 63662, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 4, 0, 'Извлеки из этого урок!', 12, 0, 100, 0, 0, 28563, 63658, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 5, 0, 'Но я уверена, что туманы рассеялись не просто так. Причина этому – ты. Тебе нужно понять, зачем ты здесь.', 12, 0, 100, 0, 0, 28566, 63661, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 6, 0, 'Из-за стены тумана сюда прибыл еще один гость – его зовут Чэнь Буйный Портер. Он – пандарен, но нездешний.', 12, 0, 100, 0, 0, 28568, 63663, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 7, 0, 'Исследуй эти земли вместе с Чэнем. Подружись с живущим здесь народом. И со временем ты найдешь ответы.', 12, 0, 100, 0, 0, 28569, 63664, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67132, 8, 0, 'Вот мы и снова встретились.', 12, 0, 100, 0, 0, 28561, 70590, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67134, 0, 0, 'Неплохо.', 12, 0, 100, 0, 0, 0, 88586, 0, 'Кэт');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67134, 1, 0, 'Well played.', 12, 0, 100, 0, 0, 0, 76673, 0, 'Kat');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67222, 0, 0, 'Медвежоночек! То есть... долго же тебя не было.', 12, 0, 100, 4, 0, 29536, 61221, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67222, 1, 0, 'Не называй меня лапушкой! Я – то есть мы – тут чуть с ума не сошли!', 12, 0, 100, 274, 0, 29537, 61222, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67222, 2, 0, 'И "кое-что" – не для меня, Хао! Достопочтенный хранитель истории уже заждался... ой!', 12, 0, 100, 274, 0, 29538, 61225, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67334, 0, 0, '%s впадает в исступление!', 16, 0, 100, 0, 0, 0, 30726, 0, 'Отважный страж Кровавого Эфеса');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67346, 0, 0, '%s впадает в исступление!', 16, 0, 100, 0, 0, 0, 30726, 0, 'Налетчик из Крепости Покорителей');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67362, 0, 0, '%s в панике пытается бежать!', 16, 0, 100, 0, 0, 0, 1150, 0, 'Стрелок из Крепости Покорителей');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67370, 0, 0, 'Я бросаю вызов всем укротителям, мастерам и любителям! Осмелитесь ли вы бросить вызов моим питомцам и одолеть их в бою?', 12, 0, 100, 70, 0, 0, 68899, 0, 'Джереми Фитиль to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67370, 1, 0, 'Укротители! Смогут ли ваши питомцы одолеть лучших из лучших на этой ярмарке?', 12, 0, 100, 70, 0, 0, 68898, 0, 'Джереми Фитиль to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67423, 0, 0, 'Помоги-и-ите!', 12, 0, 100, 0, 0, 0, 69042, 0, 'Крестьянин операции "Заслон"');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67435, 0, 0, 'Наконец-то! Тепленькое на закуску! <Коротышка из Альянса!>', 12, 0, 100, 0, 0, 0, 68955, 0, 'Ор\'дак to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67435, 1, 0, 'Хм, |3-6($c)?! Ненавижу |3-8($c)! Хотя на вкус ничего.', 12, 0, 100, 0, 0, 0, 68954, 0, 'Ор\'дак to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67461, 0, 0, 'Go ahead. Make me bleed. A true orc lives for combat!', 12, 0, 100, 1, 0, 0, 71031, 0, 'Smart Text');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (67461, 1, 0, 'THIS is the best your pathetic Alliance can muster? PAH!', 12, 0, 100, 1, 0, 0, 71028, 0, 'Smart Text');
