INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69257, 0, 0, 'Поверить не могу…', 12, 0, 100, 1, 0, 0, 72333, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69257, 1, 0, 'Послушай, я знаю, что наши народы враждуют, но если ты слепо веришь этому черному дракону, алчущему могущества – чем ты лучше своего вождя? Пожалуйста, будь осторожнее.', 12, 0, 100, 1, 0, 0, 72330, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69257, 2, 0, 'У меня холодок по спине прошел!', 12, 0, 100, 1, 0, 35307, 72377, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69257, 3, 0, 'На твоем месте я бы не стал надевать это на голову, герой.', 12, 0, 100, 6, 0, 0, 72360, 0, 'Андуин Ринн to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69257, 4, 0, 'A chill just went up my spine!', 12, 0, 100, 1, 0, 0, 72377, 0, 'Anduin Wrynn to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69257, 5, 0, 'Нет – хватит. Прекратите это немедленно!', 12, 0, 100, 5, 0, 0, 72938, 0, 'Андуин Ринн');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69257, 6, 0, 'Это же пандаренская игра. Оба игрока должны выиграть.', 12, 0, 100, 1, 0, 0, 72322, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69265, 0, 0, 'Thank you. I can find my way back to camp from here.', 12, 0, 100, 2, 0, 0, 71527, 0, 'Sunreaver Scout to Shan\'ze Soulripper');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69285, 0, 0, 'Stop interruptin\'.', 12, 0, 100, 0, 0, 0, 72155, 0, 'Zandalari Spiritweaver to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69326, 0, 0, 'Metal Lord Mono-Han prepares a whirling attack!', 41, 0, 100, 0, 0, 0, 71498, 0, 'Metal Lord Mono-Han');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69348, 1, 0, 'Что тебе от меня нужно?', 12, 0, 100, 0, 0, 0, 111488, 0, 'Мелкоклык Гнилого Клинка to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69375, 2, 0, 'Погибая, укрывающий туман испускает вспышку света, которая раскрывает все дымчатые туманы в области.', 16, 0, 100, 0, 0, 0, 72818, 0, 'Укрывающий туман');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69379, 1, 0, 'Ужин готов!', 12, 0, 100, 5, 0, 0, 72142, 0, 'Зандаларский заклинатель зверей to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69379, 3, 0, 'Назад, глупое животное!', 12, 0, 100, 22, 0, 0, 71407, 0, 'Зандаларский заклинатель зверей to Птенец небесного крикуна');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69379, 4, 0, 'Глупец!', 12, 0, 100, 18, 0, 0, 71501, 0, 'Зандаларский заклинатель зверей to Птенец небесного крикуна');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69391, 0, 0, 'О! Вот это я понимаю, это удар! Ты не $gбезнадежен:безнадежна,!', 15, 0, 100, 0, 0, 0, 71630, 0, 'Императорский хранитель ключей to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69391, 1, 0, 'Достаточно пустых просьб! Я приказываю тебе собрать мое тело.', 15, 0, 100, 0, 0, 0, 71627, 0, 'Императорский хранитель ключей to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69391, 2, 0, 'Твои умения впечатляют. Когда-нибудь из тебя выйдет $gотличный раб:отличная рабыня,.', 15, 0, 100, 0, 0, 0, 71631, 0, 'Императорский хранитель ключей to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69391, 3, 0, 'Как убивать несчастных островитян, так тут у тебя время есть. А как собирать мое великолепное тело, так нету?', 15, 0, 100, 0, 0, 0, 71626, 0, 'Императорский хранитель ключей to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69391, 4, 0, 'Я пережил сотни лет правления Властелина Грома. Хочешь узнать, как? Я никогда не заставлял своих хозяев ждать.', 15, 0, 100, 0, 0, 0, 71632, 0, 'Императорский хранитель ключей to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69391, 5, 0, 'Да сауроки, которых я видел, и то дерутся эффектнее.', 15, 0, 100, 0, 0, 0, 71629, 0, 'Императорский хранитель ключей to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69435, 0, 0, 'Кровь! Вкусно! Накорми меня!', 14, 0, 0, 0, 0, 0, 71897, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69435, 1, 0, 'Нет! Моя кровь... она... утекает...', 12, 0, 0, 0, 0, 0, 71899, 0, '');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69461, 0, 0, 'Буря поглотит тебя!', 14, 0, 100, 0, 0, 0, 71890, 0, 'Итока');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69461, 1, 0, 'Повелитель грома... защити меня!', 14, 0, 100, 0, 0, 0, 71891, 0, 'Итока');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69468, 0, 0, 'Расчисти путь от корабля к воротам на верхнем уровне – нам придется идти пешком.', 12, 0, 100, 0, 0, 35862, 72406, 0, 'Таоши to Taoshi Line Bunny');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69478, 0, 0, 'Роммат, выставь охрану. Этас, установи тотемы. Кетиэль, займись палатками и припасами.', 12, 0, 100, 0, 0, 0, 71673, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69478, 1, 0, 'Слава син\'дорай! Орда снова одержала победу!', 12, 0, 100, 0, 0, 0, 71672, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69478, 2, 0, 'Братья и сестры! Враги окружают нас со всех сторон. Только от вашей отваги зависит, победим мы или проиграем.', 12, 0, 100, 0, 0, 0, 71674, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69527, 0, 0, 'Ты $gслаб, приятель:слаба, подруга,.', 12, 0, 100, 0, 0, 0, 72146, 0, 'Зандаларский щитоносец to Тажань Чжу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69534, 0, 0, 'Я… нет… Император! Император, я подвел тебя!', 14, 0, 100, 0, 0, 36934, 71889, 0, 'Шань-Бу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69534, 1, 0, 'Ваши крики услаждают слух повелителя!', 14, 0, 100, 0, 0, 35777, 71881, 0, 'Шань-Бу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69619, 0, 0, 'Этас Похититель Солнца неловко мнется.', 16, 0, 100, 432, 0, 0, 71929, 0, 'Верховный маг Этас Похититель Солнца to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69652, 0, 0, 'Закрыть ворота! Зандалары не удержали стену.', 14, 0, 100, 0, 0, 35768, 72728, 0, 'Шань-Бу');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69717, 0, 0, 'Но это неважно. Идея Таоши слишком опасна.', 12, 0, 100, 274, 0, 35915, 71809, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69717, 1, 0, 'Ладно, Таоши. Если все согласны, то приступаем.', 12, 0, 100, 1, 0, 35916, 71814, 0, 'Тажань Чжу to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69742, 0, 0, 'Согласитесь, лорд Чжу. Только решительные действия приносят победу.', 12, 0, 100, 1, 0, 35671, 71820, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69742, 1, 0, 'Мне нравится безрассудная смелость этого плана. Если мы прорвемся к пристани, то лишим могу дальнейшей поддержки со стороны зандаларов. ', 12, 0, 100, 1, 0, 35670, 71818, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69742, 2, 0, 'А! Герой! Мы как раз говорили о тебе.', 12, 0, 100, 1, 0, 35669, 71817, 0, 'Лор\'темар Терон to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69771, 0, 0, 'Я дам вам ледяную силу северных ветров!', 14, 0, 100, 0, 0, 29024, 73071, 0, 'Поблекнувшая проекция Сюэня to World Trigger');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69774, 0, 0, 'Я могу дать тебе немногое, но это все, что у меня есть. Пусть это поможет тебе в решающий час.', 14, 0, 100, 0, 0, 29620, 59968, 0, 'Поблекнувшая проекция Чи-Цзи to World Trigger');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69777, 0, 0, 'Громите этих варваров, не сдерживая ярости!', 14, 0, 100, 0, 0, 31044, 59694, 0, 'Поблекнувшая проекция Нюцзао to World Trigger');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69779, 0, 0, 'Тебе не придется сражаться в одиночку! Обрушь мой гнев на этих чужаков!', 14, 0, 100, 0, 0, 28560, 59679, 0, 'Поблекнувшая проекция Юй-лун to World Trigger');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 0, 0, 'Властелин Грома завладел силой хранителя титанов. Мы должны отобрать ее!', 12, 0, 100, 1, 0, 0, 72375, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 1, 0, 'Принеси мне его черное, жестокое сердце. Мы окажем Пандарии услугу, а силу титанов обратим на благо всего Азерота.', 12, 0, 100, 25, 0, 0, 72376, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 2, 0, 'А я что тебе говорил? Мало кто из смертных обладает такой смелостью.', 12, 0, 100, 1, 0, 0, 72335, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 3, 0, 'Something tells me our friend has returned triumphant.', 12, 0, 100, 6, 0, 0, 72378, 0, 'Wrathion to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 4, 0, 'Я ждал твоего возвращения, герой.', 12, 0, 100, 1, 0, 0, 72374, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 5, 0, 'Полагаю, наш ордынский друг не станет сталкивать тебя со скалы.', 12, 0, 100, 6, 0, 0, 72339, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 6, 0, 'Что-то мне говорит, что наш друг вернулся с победой.', 12, 0, 100, 6, 0, 36063, 72378, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 7, 0, 'The Thunder King took within himself the powers of a Titan keeper. We must take them back!', 12, 0, 100, 1, 0, 0, 72375, 0, 'Wrathion to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 8, 0, '…Зачем ты положил эту фишку?', 12, 0, 100, 6, 0, 0, 72321, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 9, 0, 'Тун! Это наш друг?', 12, 0, 100, 0, 0, 0, 72332, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 10, 0, 'Bring me his cruel, black heart. We will be doing the Pandaren a favor, and the powers that we liberate will once again be put to the service of Azeroth.', 12, 0, 100, 25, 0, 0, 72376, 0, 'Wrathion to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 11, 0, 'I have been looking forward to your return, champion.', 12, 0, 100, 1, 0, 0, 72374, 0, 'Wrathion to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69782, 12, 0, 'Чушь какая! Зачем тогда вообще играть?!', 12, 0, 100, 1, 0, 0, 72323, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69824, 0, 0, 'Кузня Грома наша! Мы не пожалеем жизни ради ее защиты!', 14, 0, 100, 0, 0, 0, 73973, 0, 'Пиромант Шань\'цзэ');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69828, 0, 0, '|cFFFF2222[Наковальня грома]|r перегружена! Активируйте ее, чтобы |cFFFF2222|Hspell:138834|h[Разрядка]|h|r высвободила ее энергию!', 41, 0, 100, 0, 0, 0, 73026, 0, 'Небесный кузнец to Небесный кузнец');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69828, 1, 0, '|cFFFF2222[Наковальня грома]|r перегружена! Щелкните по ней, чтобы использовать |cFFFF2222|Hspell:138834|h[Раскат грома]|h|r!', 41, 0, 100, 0, 0, 0, 73003, 0, 'Небесный кузнец to Небесный кузнец');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69828, 2, 0, 'Небесный кузнец может умереть!', 41, 0, 100, 0, 0, 0, 72534, 0, 'Небесный кузнец');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (69833, 0, 0, 'Обнаружены лазутчики! Необходимо защитить Кузню Грома!', 14, 0, 100, 0, 0, 0, 73972, 0, 'Воин Шань\'цзэ');
