INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70023, 3, 0, 'А ты не $gпытался:пыталась, в этом НЕ СТОЯТЬ?! Получалось бы куда лучше!', 12, 0, 100, 0, 0, 0, 72279, 0, 'Надоедливый бес to Надоедливый бес');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70032, 0, 0, 'Это цикл жизни. Свет создал троллей, тролли создали динозавров, динозавры едят людей, троллям достается Азерот.', 12, 0, 100, 1, 0, 0, 72297, 0, 'Доктор Ян Голдблум');
