INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70297, 1, 0, 'Слушай меня, герой. Ночью с помощью прирученного тобой зверя мы долетим до пристани.', 12, 0, 100, 1, 0, 35849, 71811, 0, 'Таоши to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70297, 2, 0, 'Там мы взорвем зандаларский корабль и, воспользовавшись суматохой, проскользнем во двор и откроем ворота изнутри.', 12, 0, 100, 1, 0, 35850, 71812, 0, 'Таоши to Player');
