INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70347, 0, 0, 'Я брошу твой труп в яму с гнилью!', 14, 0, 100, 0, 0, 0, 72747, 0, 'Кромсатель Гнилого Клинка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70348, 0, 0, 'Разорвать могу на кусочки!', 14, 0, 100, 0, 0, 0, 72746, 0, 'Застрельщик Гнилого Клинка');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70476, 0, 0, 'Что могу должны были защитить? Или уничтожить? Нам не хватает последних кусочков головоломки, и ты поможешь мне найти их!', 12, 0, 100, 6, 0, 0, 72357, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70476, 1, 0, 'Мы знаем, что могу – создания титанов. Но многие вопросы остались без ответа. Зачем они были здесь? Почему в Пандарии?', 12, 0, 100, 1, 0, 0, 72356, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70476, 2, 0, 'Я буду пристально следить за тобой!', 12, 0, 100, 0, 0, 0, 72359, 0, 'Гневион to Гневион');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70476, 3, 0, 'Корона, что я дал тебе, будет твоим третьим глазом. Теперь я буду видеть то, что видишь ты. Вместе мы раскроем тайны титанов.', 12, 0, 100, 273, 0, 0, 72358, 0, 'Гневион to Player');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70480, 0, 0, 'Он объединил их, поработил другие расы Пандарии и силами рабов построил великое государство.', 12, 0, 100, 0, 0, 0, 72782, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70480, 1, 0, 'В пандаренских легендах говорится о тиране Лэй Шэне, "Властелине Грома". Двенадцать тысяч лет назад он основал империю могу.', 12, 0, 100, 0, 0, 0, 72781, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (70480, 2, 0, 'На границе империи Властелин Грома построил величественную крепость. Скоро мы ее увидим.', 12, 0, 100, 0, 0, 0, 72783, 0, 'Капитан разведчиков Эльсия to Кровавый дракондор Похитителей Солнца');
