INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73709, 1, 0, 'Гаррош скрывал под землей целый комплекс. Куда его завела жажда власти?', 14, 0, 100, 5, 0, 0, 76615, 0, 'Вол\'джин');
