INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73709, 4, 0, 'Hah. Darkspear never die.', 14, 0, 100, 1, 0, 39150, 76619, 0, 'Vol\'jin');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73709, 5, 0, 'Хм. Черное Копье не убьешь.', 14, 0, 100, 0, 0, 0, 76619, 0, 'Вол\'джин');
INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `TextRange`, `comment`) VALUES (73709, 6, 0, 'Бейн, ты и твои воины нужны тут. Наши герои попробуют выбить вождя из укрытия.', 14, 0, 100, 1, 0, 0, 76616, 0, 'Вол\'джин');
