INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES ( 3692, 1, 0, 'Oh no!  Here they come!', 12, 0, 100, 0, 0, 0, 3744, 0, 'Volcor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES ( 3692, 2, 0, 'Thank you again, $n. I\'ll make my way to the road now. When you can, find Terenthis and let him know we escaped.', 12, 0, 100, 0, 0, 0, 1236, 0, 'Volcor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES ( 3692, 3, 0, 'We made it, my friend. Remember to find Terenthis and let him know we\'re safe. Thank you again.', 12, 0, 100, 0, 0, 0, 1243, 0, 'Volcor');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES ( 3693, 0, 0, 'What is happening?', 12, 0, 0, 0, 0, 0, 13848, 0, 'Terenthis');
