INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES ( 3984, 1, 0, 'He must be overwhelmed with work at the Monastery. But still, he should have returned by now.', 12, 7, 100, 0, 0, 0, 1367, 0, 'Nancy Vishas');
