INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES ( 3985, 1, 0, 'Cleansing the world of impure scum isn\'t an easy job, Nancy. Someone\'s got to do it.', 12, 7, 100, 0, 0, 0, 1368, 0, 'Grandpa Vishas');
