INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16389, 0, 1, 'You, hold there!', 12, 0, 100, 0, 0, 0, 13798, 0, 'Spectral Apprentice - On Aggro');
