INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16389, 1, 1, 'Why...?', 12, 0, 100, 0, 0, 0, 13802, 0, 'Spectral Apprentice - On Death');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16410, 0, 0, 'You\'ll ruin everything!', 12, 0, 100, 0, 0, 0, 13844, 0, 'Spectral Retainer - On Aggro');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16410, 0, 1, 'Off with you!', 12, 0, 100, 0, 0, 0, 13845, 0, 'Spectral Retainer - On Aggro');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16410, 0, 2, 'We do not wish to be disturbed!', 12, 0, 100, 0, 0, 0, 13846, 0, 'Spectral Retainer - On Aggro');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16410, 1, 0, 'Where am I? What--', 12, 0, 100, 0, 0, 0, 13847, 0, 'Spectral Retainer - On Death');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16410, 1, 1, 'What is happening?', 12, 0, 100, 0, 0, 0, 13848, 0, 'Spectral Retainer - On Death');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16410, 1, 2, 'I mustn\'t be afraid....', 12, 0, 100, 0, 0, 0, 13849, 0, 'Spectral Retainer - On Death');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16411, 0, 0, 'I\'ll chop you into minced meat!', 12, 0, 100, 0, 0, 0, 13861, 0, 'Spectral Chef - On Aggro');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16411, 0, 1, 'I\'ll gut you like a fish!', 12, 0, 100, 0, 0, 0, 13862, 0, 'Spectral Chef - On Aggro');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16411, 0, 2, 'I\'ll carve the meat from your bones!', 12, 0, 100, 0, 0, 0, 13863, 0, 'Spectral Chef - On Aggro');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16411, 1, 0, 'I have served to the last....', 12, 0, 100, 0, 0, 0, 13864, 0, 'Spectral Chef - On Death');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16411, 1, 1, 'This isn\'t the end; it can\'t be!', 12, 0, 100, 0, 0, 0, 13865, 0, 'Spectral Chef - On Death');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16411, 1, 2, 'I tried, master. I tried.', 12, 0, 100, 0, 0, 0, 13866, 0, 'Spectral Chef - On Death');
