INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17587, 3, 3, 'Hrm, azure snapdragons? Where do they come up with these names? Daedal has gone mad!', 12, 35, 100, 0, 0, 0, 14177, 0, '17587');
