INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 7, 0, '1...', 12, 0, 100, 0, 0, 0, 14655, 0, 'npc_demolitionist_legoso SAY_LEGOSO_8');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 8, 0, 'Don\'t get too excited, hero, that was the easy part. The challenge lies ahead! Let\'s go.', 12, 0, 100, 1, 0, 0, 14656, 0, 'npc_demolitionist_legoso SAY_LEGOSO_9');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 9, 0, 'What in the Nether is that?!?!', 12, 0, 100, 5, 0, 0, 14657, 0, 'npc_demolitionist_legoso SAY_LEGOSO_10');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 10, 0, 'Be ready for anything.', 12, 0, 100, 1, 0, 0, 14658, 0, 'npc_demolitionist_legoso SAY_LEGOSO_11');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 11, 0, 'Blessed Light! She\'s siphoning energy right out of the Vector Coil!', 12, 0, 100, 53, 0, 0, 14659, 0, 'npc_demolitionist_legoso SAY_LEGOSO_12');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 12, 0, 'Cover me, we have to do this quickly. Once I blow the support on this side, it will disrupt the energy beams and she\'ll break out! I doubt very much that she\'ll be happy to see us.', 12, 0, 100, 1, 0, 0, 14660, 0, 'npc_demolitionist_legoso SAY_LEGOSO_13');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 13, 0, 'I\'ve almost got it! Just a little more time...', 12, 0, 100, 0, 0, 0, 14661, 0, 'npc_demolitionist_legoso SAY_LEGOSO_14');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 14, 0, 'Take cover and be ready for the fight of your life!', 12, 0, 100, 5, 0, 0, 14662, 0, 'npc_demolitionist_legoso SAY_LEGOSO_15');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 15, 0, '3...', 12, 0, 100, 0, 0, 0, 14653, 0, 'npc_demolitionist_legoso SAY_LEGOSO_16');
