INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 18, 0, 'Holy mother of O\'ros!', 12, 0, 100, 5, 0, 0, 14663, 0, 'npc_demolitionist_legoso SAY_LEGOSO_19');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 19, 0, 'I... I can\'t believe it\'s over. You did it! You\'ve destoyed the blood elves and their leader!', 12, 0, 100, 1, 0, 0, 14664, 0, 'npc_demolitionist_legoso SAY_LEGOSO_20');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 20, 0, 'Get back to Blood Watch. I\'ll see you there...', 12, 0, 100, 1, 0, 0, 14665, 0, 'npc_demolitionist_legoso SAY_LEGOSO_21');
