INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20913, 0, 0, 'Now to begin the repairs.', 12, 0, 100, 0, 0, 0, 18651, 0, 'Tashar');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (20913, 1, 0, 'It\'s working! Ghabar will be pleased to hear the news.', 12, 0, 100, 0, 0, 0, 18652, 0, 'Tashar');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21046, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21049, 0, 0, 'You have damned us all... We are lost.', 12, 0, 100, 20, 0, 0, 18700, 0, 'Spirit of the Past');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21049, 0, 1, 'Stop! You must stop!', 12, 0, 100, 20, 0, 0, 18698, 0, 'Spirit of the Past');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21049, 0, 2, 'Do not do this, Gul\'dan! The elements will never forgive us!', 12, 0, 100, 20, 0, 0, 18697, 0, 'Spirit of the Past');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21049, 0, 3, 'Think of our younglings, Gul\'dan! You will destroy us all!', 12, 0, 100, 20, 0, 0, 18699, 0, 'Spirit of the Past');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21049, 0, 4, 'We will take from the land if it refuses to give!', 12, 0, 100, 15, 0, 0, 18696, 0, 'Spirit of the Past');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21049, 0, 5, 'Tear the land asunder, Gul\'dan! Sever the tie!', 12, 0, 100, 15, 0, 0, 18694, 0, 'Spirit of the Past');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21049, 0, 6, 'They have abandoned us! Do not forget!', 12, 0, 100, 15, 0, 0, 18695, 0, 'Spirit of the Past');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21060, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Enrage');
