INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21151, 0, 0, 'ACK! That\'s the worst thing I\'ve ever tasted! I wouldn\'t let my ram drink that!', 12, 0, 100, 1, 0, 0, 18830, 0, 'Borgrim Stouthammer');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21181, 0, 0, 'Cyrukh has awoken!', 14, 0, 100, 0, 0, 0, 19298, 0, 'Cyrukh the Firelord');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21181, 1, 0, 'Cyrukh comes for you!', 14, 0, 100, 0, 0, 0, 19301, 0, 'Cyrukh the Firelord');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21181, 1, 1, 'You will suffer eternally!', 14, 0, 100, 0, 0, 0, 19299, 0, 'Cyrukh the Firelord');
