INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22807, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22820, 0, 0, 'Akama, my friend!  Have you been informed?  I was found out by Vashj\'s followers.  Our secret is in peril!', 12, 0, 100, 0, 0, 11611, 20498, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22820, 1, 0, 'Illidan is looking for me.  I am not safe here... none of us are.', 12, 0, 100, 1, 0, 11612, 20502, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22820, 2, 0, 'Illidan will find me and when he does... he will pry the Ashtongue secret from me - such is the power of the Betrayer!  No... there is only one way.  My usefulness to the cause has come to an end. I must venture into the spirit world!', 12, 0, 100, 6, 0, 11613, 20499, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22820, 3, 0, 'My decision is final.  I will not be dissuaded.  I have devoted my life to our plan.  To jeopardize it now would betray all who have died for the cause long before me.', 12, 0, 100, 274, 0, 11614, 20500, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22820, 4, 0, 'I thank you, Akama.  You honor me by allowing me this moment, surrounded by my brothers.  Farewell, friend.  Guide our people well.', 12, 0, 100, 2, 0, 11615, 20501, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22832, 0, 0, 'There are few left in the world who can prepare the Ward of Waking, $N. We are fortunate that I am among them.', 12, 0, 100, 1, 0, 0, 20516, 0, 'Morthis Whisperwing');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22832, 1, 0, '%s kneels, filling the vial with water from the moonwell.', 16, 0, 100, 0, 0, 0, 20512, 0, 'Morthis Whisperwing');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22832, 2, 0, '%s combines the herbs with the moonwell water to create the Ward of Waking', 16, 0, 100, 0, 0, 0, 20517, 0, 'Morthis Whisperwing');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22832, 3, 0, 'The potion is finished. I can only hope Clintar forgives us for interrupting his sojourn in the Emerald Dream.', 12, 0, 100, 1, 0, 0, 20513, 0, 'Morthis Whisperwing');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22862, 0, 2, 'I\'ve a message for your master, scum!', 12, 0, 100, 0, 0, 0, 20672, 0, 'Caalen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22862, 0, 1, 'Illidan\'s lapdogs! Destroy them all!', 12, 0, 100, 0, 0, 0, 20669, 0, 'Caalen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22862, 0, 0, 'Come closer, demon! Death awaits!', 12, 0, 100, 0, 0, 0, 20670, 0, 'Caalen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22862, 0, 3, 'Pathetic worm... your master\'s days are over!', 12, 0, 100, 0, 0, 0, 20671, 0, 'Caalen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22862, 0, 4, 'For Xi\'ri!  For the Sha\'tar!', 12, 0, 100, 0, 0, 0, 20667, 0, 'Caalen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22862, 0, 5, 'Your end is at hand, Illidari!', 12, 0, 100, 0, 0, 0, 20668, 0, 'Caalen');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22864, 0, 0, 'Onward, Scryers! Show Illidan\'s lackeys the temper of our steel!', 14, 0, 100, 0, 0, 0, 20696, 0, 'Fyra Downstar');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22865, 0, 0, 'You disappoint me, Akama.  I wanted to question the treacherous worm myself!  I\'m beginning to question your allegiance, Broken.', 12, 0, 100, 1, 0, 11608, 20628, 0, 'Illidan\'s Presence to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22865, 1, 0, 'I do not sense lies in your voice, Akama.  That Kael\'thas would betray me does not come as a big surprise - I\'m not as oblivious as some would think. That one of your own was involved with him puts your loyalties into question.', 12, 0, 100, 5, 0, 11609, 20629, 0, 'Illidan\'s Presence to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22865, 2, 0, 'Send your Ashtongue into Tempest Keep and slay his most prized possession, the phoenix known as Al\'ar.  I must know that you\'re not on his side.  Do not think of betraying me, Broken.  We both know who owns your soul!', 12, 0, 100, 1, 0, 11610, 20630, 0, 'Illidan\'s Presence to Player');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22889, 0, 0, 'A %s erupts into the waking world from the Emerald Dream!', 16, 0, 100, 0, 0, 0, 20562, 0, 'Manifest Nightmare');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22902, 0, 0, 'A %s erupts into the waking world from the Emerald Dream!', 16, 0, 100, 0, 0, 0, 20562, 0, 'Phantasmal Lash');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22910, 0, 0, 'Argh!  I will shred your soul and grind it to dust!', 14, 0, 100, 0, 0, 0, 20726, 0, 'Skulloc Soulgrinder');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22941, 0, 0, 'All hail, $n, the new $G King : Queen; of the ogres!!!', 12, 0, 100, 0, 0, 0, 20791, 2, 'Mog\'dorg the Wizened');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22981, 0, 0, 'Come closer, young one. There\'s nothing to fear from me or $n.', 12, 0, 100, 1, 0, 0, 20738, 0, 'Watcher Elaira');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22981, 1, 0, 'I\'m going to train you to help $N with very important task. After that, I promose you\'ll be free to return home.', 12, 0, 100, 1, 0, 0, 20740, 0, 'Watcher Elaira');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22981, 2, 0, 'You will hear the call when you are needed. Fly east, to Skettis, and wait there for $N\'s signal.', 12, 0, 100, 1, 0, 0, 20751, 0, 'Watcher Elaira');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22982, 0, 0, '$n, do you feel the need -- the need for speed?', 12, 0, 100, 0, 0, 0, 21886, 0, 'Skyguard Navigator');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22982, 0, 1, '$n, how\'s your mount holding up?  Pretty rough up there these days?', 12, 0, 100, 0, 0, 0, 21897, 0, 'Skyguard Navigator');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (22982, 0, 3, 'Heh, the skies aren\'t so friendly with $n flying them!', 12, 0, 100, 0, 0, 0, 21895, 0, 'Skyguard Navigator');
