INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23042, 0, 0, 'Rest now, Adaris. You need to recover your strength.', 12, 0, 100, 0, 0, 0, 21918, 0, 'Severin');
