INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23284, 3, 0, 'Overlord Mor\'ghor, I presume... A pleasure to finally make your acquaintance.', 12, 0, 100, 0, 0, 0, 21305, 0, 'Sinestra');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23284, 4, 0, 'I am Lady Sinestra.', 12, 0, 100, 0, 0, 0, 21306, 0, 'Sinestra');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23284, 5, 0, 'You have no doubt heard about Nefarian\'s failures on Azeroth... While he has fallen, the experiment continues. My master... He continues the work that his progeny began.', 12, 0, 100, 0, 0, 0, 21308, 0, 'Sinestra');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23284, 6, 0, '$s nods.', 16, 0, 100, 0, 0, 0, 21309, 0, 'Sinestra');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23284, 7, 0, 'You were once a chief lieutenant, Mor\'ghor. Your work in Grim Batol is not easily forgotten.', 12, 0, 100, 0, 0, 0, 21311, 0, 'Sinestra');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23284, 8, 0, 'Now... We need the eggs that you recovered. The Netherwing eggs. They are, after all, a product of the master. We will pay whatever price that you ask.', 12, 0, 100, 0, 0, 0, 21312, 0, 'Sinestra');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23284, 9, 0, 'The master will be most pleased with this news. The Black Dragonflight will provide you all that you ask. You will be allowed to ride upon the backs of our drakes as needed. ', 12, 0, 100, 0, 0, 0, 21315, 0, 'Sinestra');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (23284, 10, 0, 'I thank you for your graciousness, Mor\'ghor. I must now take my leave.', 12, 0, 100, 0, 0, 0, 21316, 0, 'Sinestra');
