INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 0, 1, 'State your profession.', 12, 7, 100, 0, 0, 0, 24382, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 0, 2, 'Yes, then.  What is your trade?', 12, 7, 100, 0, 0, 0, 24383, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 0, 3, 'Your previous line of work, recruit?', 12, 7, 100, 0, 0, 0, 24385, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 0, 4, 'Your profession?', 12, 7, 100, 0, 0, 0, 24384, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 1, 0, 'Yes, you\'re well seasoned in your field of work.  Report to the civilian liaison at once, we need more like you!', 12, 7, 100, 273, 0, 0, 24361, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 1, 1, 'I thought we had enough in your line of work, but it looks like we have some openings.  Report to the civilian liaison.', 12, 7, 100, 273, 0, 0, 24360, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 1, 2, 'I do have some openings in your line of work.  Report to the civilian liaison.', 12, 7, 100, 273, 0, 0, 24359, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 1, 3, 'Yeah.  We could use some more of you.  Report to the civilian liaison for work assignment.', 12, 7, 100, 273, 0, 0, 24358, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 2, 0, 'Not anymore!  Here\'s your sword.  Report to the barracks for duty!', 12, 7, 100, 397, 0, 0, 24363, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 2, 1, 'Afraid not, friend.  Here\'s your sword and shield.  Report to the barracks for duty.', 12, 7, 100, 397, 0, 0, 24365, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 2, 2, 'You\'re breaking my heart - I\'ve quotas to fill, you know?  Can you wield a sword?  Off to the barracks.', 12, 7, 100, 397, 0, 0, 24367, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 2, 3, 'With a sword arm like that?  I\'m afraid we need you in the front lines, my friend.  Report to the barracks.', 12, 7, 100, 397, 0, 0, 24366, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (25222, 2, 4, 'Looks like we have room for one more... no, my mistake.  That\'s a seven not a one.  Hope you\'re good with a sword - report to the barracks.', 12, 7, 100, 397, 0, 0, 24368, 0, 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
