INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (26786, 0, 2, 'You are a fool to fight Kurun\'s hopeless battle!', 12, 0, 100, 0, 0, 0, 25986, 0, 'Iron Rune Avenger');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (26786, 0, 3, 'You will pay for your interference!', 12, 0, 100, 0, 0, 0, 25985, 0, 'Iron Rune Avenger');
INSERT INTO `creature_text` (`entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (26786, 0, 4, 'Your life is forfeit, ally of stone!', 12, 0, 100, 0, 0, 0, 25987, 0, 'Iron Rune Avenger');
